INSERT INTO seqtext VALUES(1,3,1,'Une lgre brise faisait tinter les branches dgarnies comme des carillons en bois.',1,0,0);
INSERT INTO seqtext VALUES(1,4,1,'Il s''agissait d''un endroit populaire pour les couples pendant l''t. Les arbres feuillus permettaient l''existence d''un superbe auvent vert, loin de la vue des professeurs et des tudiants.',1,0,0);
INSERT INTO seqtext VALUES(1,5,1,'Mais  ce moment-l, en fin d''hiver, c''tait comme si j''tais debout sous une pile de petit bois.',1,0,0);
INSERT INTO seqtext VALUES(1,6,1,'Je soufflais sur mes mains et les frottais nerveusement ensemble pour les empcher de s''engourdir dans ce froid.',1,0,0);
INSERT INTO seqtext VALUES(1,7,1,'Combien de temps au juste tais-je cens attendre ici ? J''tais sr que le message disait 16 heures.',12,0,0);
INSERT INTO seqtext VALUES(1,8,1,'Ah oui... le message... gliss entre les pages de mon livre de maths alors que je ne regardais pas.',1,0,0);
INSERT INTO seqtext VALUES(1,9,1,'Aussi loin que pouvaient aller les clichs, je prfrais la lettre dans le casier, mais au moins, cette faon de faire montre qu''elle avait un peu d''initiative.',1,0,0);
INSERT INTO seqtext VALUES(1,10,1,'Pendant que je mditais sur le sens de la note, les chutes de neige s''paississaient.',1,0,0);
INSERT INTO seqtext VALUES(1,11,1,'Les flocons de neige tombant silencieusement du ciel taient le seul signe du temps qui passait dans ce monde stagnant.',1,0,0);
INSERT INTO seqtext VALUES(1,12,1,'Leur lente descente sur la fort gele faisait penser que le temps s''tait ralenti.',1,0,0);
INSERT INTO seqtext VALUES(1,13,1,'Le bruissement des pas sur la neige me fit sursauter, interrompant ce moment de calme. Quelqu''un approchait par derrire.',1,0,0);
INSERT INTO seqtext VALUES(1,14,1,'Salut... Hisao ? Tu es venu ?',3,0,0);
INSERT INTO seqtext VALUES(1,15,1,'Une question hsitante, presque inaudible.',1,0,0);
INSERT INTO seqtext VALUES(1,16,1,'Cependant, je reconnus la propritaire de cette dlicate voix instantanment.',1,0,0);
INSERT INTO seqtext VALUES(1,17,1,'Je sentis mon cur rater un battement.',1,0,0);
INSERT INTO seqtext VALUES(1,18,1,'C''est une voix que j''avais entendue des centaines de fois, mais jamais d''une autre faon qu''en prtant une oreille indiscrte lors d''une conversation.',1,0,0);
INSERT INTO seqtext VALUES(1,19,1,'Je me retournai pour faire face  cette voix, la voix de mes rves, et mon cur commena  s''emballer.',1,0,0);
