INSERT INTO seqtext VALUES(2,4,3,'',27,400,10);
INSERT INTO seqtext VALUES(2,4,1,'Pendant tout ce temps, j''ai probablement pu compter sur les doigts d''une main le nombre de fois que j''ai quitt cette chambre d''hpital sans surveillance.',2,0,0);
INSERT INTO seqtext VALUES(2,5,1,'Quatre mois est un dlai assez long quand on reste seul avec ses penses. Donc, j''ai eu beaucoup de temps pour m''habituer  ma situation.',2,0,0);
INSERT INTO seqtext VALUES(2,6,1,'Arythmie.',2,0,0);
INSERT INTO seqtext VALUES(2,7,1,'Un mot trange. Un tranger, un alien. Un avec qui personne ne voudrait partager sa chambre.',2,0,0);
INSERT INTO seqtext VALUES(2,8,1,'Une maladie rare. Elle force le cur  agir de manire erratique et  parfois battre trop vite. Elle peut tre fatale.',2,0,0);
INSERT INTO seqtext VALUES(2,9,1,'Apparemment, je l''ai depuis longtemps. Ils ont dit que c''tait un miracle que j''aie pu aller bien pendant si longtemps sans que rien ne se passe.',2,0,0);
INSERT INTO seqtext VALUES(2,10,1,'Est-ce vraiment un miracle ? Je crois que c''tait cens me faire me sentir mieux; plus reconnaissant pour ma vie.',2,0,0);
INSERT INTO seqtext VALUES(2,11,1,'a ne fait vraiment rien pour me remonter le moral.',2,0,0);
INSERT INTO seqtext VALUES(2,12,1,'Mes parents, je crois, ont t plus choqus par la nouvelle que je ne l''ai t. Ils ont pratiquement eu deux infarctus chacun.',2,0,0);
INSERT INTO seqtext VALUES(2,13,1,'J''avais dj eu une journe pour tout digrer. Pour eux c''tait tout frais. Ils taient mme prts  vendre notre maison pour payer un traitement.',2,0,0);
INSERT INTO seqtext VALUES(2,14,1,'Bien sr, il n''y a pas de traitement.',2,0,0);
INSERT INTO seqtext VALUES(2,15,1,'En raison de la dcouverte tardive de ce... problme, j''ai d rester  l''hpital, pour rcuprer du traitement.',2,0,0);
INSERT INTO seqtext VALUES(2,16,1,'Lors de ma premire admission, je me suis senti comme perdu...',2,0,0);
INSERT INTO seqtext VALUES(2,17,1,'Pendant environ une semaine, ma chambre a t pleine de fleurs, de ballons et de cartes.',2,0,0);
INSERT INTO seqtext VALUES(2,18,1,'Mais les visiteurs et les cadeaux de rtablissement ont rapidement diminu jusqu'' finir par disparatre.',2,0,0);
INSERT INTO seqtext VALUES(2,19,1,'J''ai ralis que la seule raison pour laquelle j''avais eu autant de cartes et de fleurs tait le fait qu''envoyer leur sympathie avait t transform en projet de classe.',2,0,0);
INSERT INTO seqtext VALUES(2,20,1,'Peut-tre que certaines personnes compatissaient vraiment, mais j''en doute. Mme au dbut, j''avais  peine eu quelques visiteurs.  la fin du premier mois, seuls mes parents venaient rgulirement.',2,0,0);
INSERT INTO seqtext VALUES(2,21,1,'Iwanako fut la dernire  arrter ses visites.',2,0,0);
INSERT INTO seqtext VALUES(2,22,1,'Aprs six semaines, je ne l''ai plus jamais revue. Nous n''avions jamais eu grand-chose  nous dire lors de ces visites, de toute faon.',2,0,0);
INSERT INTO seqtext VALUES(2,23,1,'Nous n''avions jamais reparl de ce qui s''tait pass entre nous en ce jour de neige.',2,0,0);
INSERT INTO seqtext VALUES(2,24,1,'L''hpital ?',2,0,0);
INSERT INTO seqtext VALUES(2,25,1,'Ce n''est pas vraiment un endroit o j''aimerais vivre.',2,0,0);
INSERT INTO seqtext VALUES(2,26,1,'Les mdecins et les infirmires sont trop impersonnels et sans expressions.',2,0,0);
INSERT INTO seqtext VALUES(2,27,1,'Je suppose que c''est parce qu''ils sont presss et qu''ils ont un million d''autres patients qui les attendent, mais a me met mal  l''aise.',2,0,0);
INSERT INTO seqtext VALUES(2,28,1,'Pendant les deux premiers mois, j''ai demand au chef de cardiologie  chaque fois que je le voyais une estimation approximative du moment o je serais en mesure de partir.',2,0,0);
INSERT INTO seqtext VALUES(2,29,1,'Il n''a jamais rpondu  quoi que ce soit de manire directe, mais me disait d''attendre de voir si le traitement et les chirurgies faisaient effet.',2,0,0);
INSERT INTO seqtext VALUES(2,30,1,'Ainsi, j''observais paresseusement la cicatrice que ces chirurgies avaient laisse sur ma poitrine et qui changeait lentement avec le temps, laissant penser  une sorte de mauvais prsage.',2,0,0);
INSERT INTO seqtext VALUES(2,31,1,'Je continue de demander au chef de cardiologie des nouvelles  propos de ma sortie, mais mes esprances sont assez basses, maintenant je ne suis plus du quand je ne reois pas de rponse. La faon avec laquelle il tourne autour du sujet montre qu''il y a au moins un espoir.',2,0,0);
INSERT INTO seqtext VALUES(2,32,1,' un moment j''ai arrt de regarder la tlvision. Je ne sais pas pourquoi, je l''ai juste fait.',2,0,0);
INSERT INTO seqtext VALUES(2,33,1,'Peut-tre tait-ce une mauvaise faon d''chapper  ma situation.',2,0,0);
INSERT INTO seqtext VALUES(2,34,1,'J''ai commenc  lire  la place. Il y a une petite bibliothque  l''hpital, mme si elle ressemblait plus  un kiosque pour magazines. J''ai commenc  tailler mon chemin  travers elle, une petite pile  la fois. Aprs les avoir consomms, j''y retournais pour en prendre d''autres.',2,0,0);
INSERT INTO seqtext VALUES(2,35,1,'Il s''est avr que j''aimais lire et je pense que je suis devenu un peu accro. J''ai commenc  me sentir nu sans un livre dans les mains.',2,0,0);
INSERT INTO seqtext VALUES(2,36,1,'Mais j''aimais les histoires.',2,0,0);
INSERT INTO seqtext VALUES(2,37,1,'C''est  a que ressemblait ma vie.',2,0,0);
INSERT INTO seqtext VALUES(2,38,1,'Les jours devinrent de plus en plus durs  distinguer les uns des autres, n''ayant pour unique diffrence que le livre que je lisais et le temps qu''il faisait dehors. Le temps tait comme une sorte de masse visqueuse me retenant prisonnier  l''intrieur.',2,0,0);
INSERT INTO seqtext VALUES(2,39,1,'Une semaine pouvait passer sans que je ne m''en aperoive vraiment.',2,0,0);
INSERT INTO seqtext VALUES(2,40,1,'Parfois, je me posais en ralisant que je ne savais pas quel jour de la semaine on tait.',2,0,0);
INSERT INTO seqtext VALUES(2,41,1,'Mais d''autres fois, toutes ces choses qui m''entouraient venaient s''craser douloureusement sur ma conscience, malgr la barrire de nonchalance que j''avais mise en place.',2,0,0);
INSERT INTO seqtext VALUES(2,42,1,'Les pages de mon livre commenaient  tre coupantes et brlantes, et la lourdeur dans ma poitrine devenait si difficile  porter que je devais poser mon livre et simplement m''allonger pendant un certain temps, en regardant le plafond, comme si j''allais pleurer.',2,0,0);
INSERT INTO seqtext VALUES(2,43,1,'Mais cela n''tait arriv que rarement.',2,0,0);
