INSERT INTO seqtext VALUES(2,45,1,'Aujourd''hui, le mdecin arrive en me lanant un sourire. Il semble excit, mais pas trop. Comme s''il essayait de faire un effort pour tre heureux  ma place.',1,0,0);
INSERT INTO seqtext VALUES(2,46,1,'Mes parents sont l. Cela fait quelques jours que je ne les ai pas vus. Ils sont tous les deux plutt bien habills. Est-ce que c''est cens tre une sorte d''occasion spciale ? Ce n''est pas une fte.',1,0,0);
INSERT INTO seqtext VALUES(2,47,1,'Il y a ce rituel que le chef de cardiologie a. Il prend son temps, trie ses papiers, puis les met de ct comme pour faire un point sur l''inutilit de ce qu''il vient de faire.',1,0,0);
INSERT INTO seqtext VALUES(2,48,1,'Puis il s''assoit sur le bord du lit  ct du mien. Il me regarde dans les yeux un instant.',1,0,0);
INSERT INTO seqtext VALUES(2,49,1,'Bonjour, Hisao. Comment vas-tu aujourd''hui ?',5,0,0);
INSERT INTO seqtext VALUES(2,50,1,'Je ne lui rponds pas, mais je lui montre un lger sourire, en rponse au sien.',1,0,0);
