INSERT INTO seqtext VALUES(2,54,1,'Tant que a...',6,0,0);
INSERT INTO seqtext VALUES(2,55,1,'Je la lui prends des mains et y jette un il par moi-mme, me sentant engourdi. Comment je suis suppos ragir  a ?',1,0,0);
INSERT INTO seqtext VALUES(2,56,2,'',43,0,0);
INSERT INTO seqtext VALUES(2,57,1,'La liste ridiculement longue de mdicaments me regarde fixement depuis le papier, ce qui me parat insurmontable. Ils se mlangent tous dans un ocan de lettres.',1,0,0);
INSERT INTO seqtext VALUES(2,58,1,'C''est insens.',1,0,0);
INSERT INTO seqtext VALUES(2,59,1,'Les effets secondaires, les effets indrisables, les contre-indications et les dosages sont numrs ligne aprs ligne avec une prcision glaciale.',1,0,0);
