INSERT INTO seqtext VALUES(2,64,1,'Je crains que ce soit le mieux que nous puissions faire  ce stade.',5,0,0);
INSERT INTO seqtext VALUES(2,65,1,'Toutefois, de nouveaux mdicaments sont constamment mis au point, donc je ne serais pas surpris de voir cette liste se raccourcir au fil des annes.',5,0,0);
