INSERT INTO seqtext VALUES(2,69,1,'S''il te plat, calme-toi, Hisao. coute ce que le docteur a  dire...',6,0,0);
INSERT INTO seqtext VALUES(2,70,1,'Me calmer ? La faon dont il l''a dit m''indique qu''il savait que je ne voudrais pas. Je vais suivre un enseignement  domicile ?',1,0,0);
