INSERT INTO seqtext VALUES(2,72,1,'Nous comprenons tous que ton ducation est primordiale, mais je ne pense pas qu''il serait prudent pour toi d''tre sans surveillance.',5,0,0);
INSERT INTO seqtext VALUES(2,73,1,'Du moins tant que nous ne sommes pas srs que tes mdicaments soient adapts.',5,0,0);
INSERT INTO seqtext VALUES(2,74,1,'Alors, j''ai parl  tes parents au sujet d''un transfert.',5,0,0);
INSERT INTO seqtext VALUES(2,75,1,'C''est une cole qui s''appelle l''Acadmie Yamaku et qui est spcialise dans le traitement des tudiants handicaps.',5,0,0);
