INSERT INTO seqtext VALUES(3,18,1,'Je me demande si le sentiment est rel ou caus par mes attentes d''une cole pour handicaps.',1,0,0);
INSERT INTO seqtext VALUES(3,19,1,'En parlant de a, je ne vois personne d''autre ici. C''est un peu surnaturel.',1,0,0);
