INSERT INTO seqtext VALUES(3,21,1,'Le bruit des arbres dans le vent et les teintes vertes rflchissantes tout autour de moi attirent mon attention.',1,0,0);
INSERT INTO seqtext VALUES(3,22,1,'a me fait encore penser  des hpitaux, ils disent que les salles d''oprations sont peintes en vert parce que le vert est une couleur apaisante.',1,0,0);
INSERT INTO seqtext VALUES(3,23,1,'Alors, pourquoi suis-je si inquiet, en dpit de toute cette verdure ?',1,0,0);
INSERT INTO seqtext VALUES(3,24,1,'',1,0,0);
