INSERT INTO seqtext VALUES(8,13,1,'Comme elle me voit la regarder aussi, elle couvre son visage de ses mains comme si a la rendait invisible.',1,0,0);
INSERT INTO seqtext VALUES(8,14,1,'Il y a un garon avec une canne, appuy contre les casiers  l''arrire de la classe. C''est trange de voir quelqu''un de si jeune avec une canne.',1,0,0);
