INSERT INTO seqtext VALUES(20,29,4,'misha',0,0,0);
INSERT INTO seqtext VALUES(20,30,2,'',55,0,0);
INSERT INTO seqtext VALUES(20,31,1,'Nous nous mlons aux tudiants circulant entre la cage d''escalier et l''extrieur, avec les filles suivant des lves de classes suprieures qui sont dans le mme couloir que le ntre.',1,0,0);
INSERT INTO seqtext VALUES(20,32,2,'',186,0,0);
INSERT INTO seqtext VALUES(20,33,1,'Quand nous arrivons  l''extrieur, les filles continuent leur chemin vers le plus petit btiment juste  ct de celui de l''cole.',1,0,0);
