INSERT INTO seqtext VALUES(20,35,3,'shizu',111,560,10);
INSERT INTO seqtext VALUES(20,35,3,'misha',541,240,10);
INSERT INTO seqtext VALUES(20,35,1,'',17,0,0);
INSERT INTO seqtext VALUES(20,36,1,'C''est le btiment secondaire ici. Il y a un paquet de trucs importants et srieux  l''intrieur, comme le bureau de la Fondation Yamaku et tous les bureaux des infirmiers. Ils ont mme une piscine !',18,0,0);
INSERT INTO seqtext VALUES(20,37,1,'En quoi c''est srieux ?',12,0,0);
