INSERT INTO seqtext VALUES(20,38,1,'',17,0,0);
INSERT INTO seqtext VALUES(20,39,3,'misha',488,240,10);
INSERT INTO seqtext VALUES(20,39,1,'Ne sois pas idiot Hicchan ! C''est pour la thrapie physique bien sr.',18,0,0);
INSERT INTO seqtext VALUES(20,40,3,'misha',584,240,10);
INSERT INTO seqtext VALUES(20,40,1,'Quoi qu''il en soit, toutes les installations du personnel infirmier sont ici aussi. Le bureau de l''infirmier chef est au premier tage.',18,0,0);
INSERT INTO seqtext VALUES(20,41,3,'misha',204,240,10);
INSERT INTO seqtext VALUES(20,41,3,'shizu',111,560,10);
INSERT INTO seqtext VALUES(20,41,1,'Tu t''en sortiras  partir d''ici, hein~ ? On dois y aller, alors ! A demain !',18,0,0);
INSERT INTO seqtext VALUES(20,42,4,'shizu',0,0,0);
INSERT INTO seqtext VALUES(20,43,4,'misha',0,0,0);
INSERT INTO seqtext VALUES(20,44,1,'Un btiment entier pour des trucs qui n''ont rien  voir avec la scolarit ?',1,0,0);
INSERT INTO seqtext VALUES(20,45,1,'J''imagine que c''est ncessaire pour un endroit comme ici.',1,0,0);
INSERT INTO seqtext VALUES(20,46,2,'',117,0,0);
INSERT INTO seqtext VALUES(20,47,1,'Je rentre dans le btiment, esprant que a ne sera vraiment qu''une visite rapide comme le professeur l''a dit.',1,0,0);
INSERT INTO seqtext VALUES(20,48,1,'Sur une porte blanche  gauche il y a une croix verte avec le texte Infirmier Chef et une plaque avec un nom.',1,0,0);
INSERT INTO seqtext VALUES(20,49,1,'Une voix  l''intrieur rpond presque immdiatement aprs que j''aie frapp  la porte, mais je ne comprends pas ce qu''elle dit.',1,0,0);
INSERT INTO seqtext VALUES(20,50,1,'a sonnait un peu comme une invitation  ouvrir la porte, alors je choisis d''entrer.',1,0,0);
INSERT INTO seqtext VALUES(20,51,2,'',545,0,0);
INSERT INTO seqtext VALUES(20,52,1,'La pice n''est pas grande et a sent bizarre, mais un hommme qui semble amical se retourne sur sa chaise pour me faire face quand j''entre.',1,0,0);
INSERT INTO seqtext VALUES(20,53,1,'Son bureau est propre et bien rang, mais la poubelle sous la table dborde d''accessoires mdicaux utiliss et il y a au moins une douzaine de cercles de tasses de caf parpills sur son bureau.',1,0,0);
INSERT INTO seqtext VALUES(20,54,3,'nurse',305,400,10);
INSERT INTO seqtext VALUES(20,54,1,'Bien le bonjour. Que puis-je faire pour toi aujourd''hui ?',42,0,0);
INSERT INTO seqtext VALUES(20,55,1,'Il semble jeune et srieux, mais les fossettes de ses joues repoussent au loin cette impression quand il sourit.',1,0,0);
INSERT INTO seqtext VALUES(20,56,1,'Hum, vous tes l''infirmier ?',12,0,0);
INSERT INTO seqtext VALUES(20,57,3,'nurse',206,400,10);
INSERT INTO seqtext VALUES(20,57,1,'Il sourit comme une personne qui a entendu cette mme question des centaines de fois.',1,0,0);
INSERT INTO seqtext VALUES(20,58,1,'Mais oui, c''est moi. C''est crit sur la porte, non ?',42,0,0);
INSERT INTO seqtext VALUES(20,59,1,'Tu peux m''appeler par mon nom ou juste infirmier comme tout le monde.',42,0,0);
INSERT INTO seqtext VALUES(20,60,1,'Bien sr. Je sors de ma confusion, ralisant que je devrais probablement saisir sa main tendue.{w} Sa poigne de main est plutt ferme et amicale.',1,0,0);
INSERT INTO seqtext VALUES(20,61,1,'Bien... euh, je suis le nouvel tudiant et mon professeur principal m''a dit de venir vous voir. Je m''appelle Hisao Nakai.',12,0,0);
INSERT INTO seqtext VALUES(20,62,1,'Ses yeux s''illuminent  la rvlation et il claque des doigts.',1,0,0);
INSERT INTO seqtext VALUES(20,63,3,'nurse',461,400,10);
INSERT INTO seqtext VALUES(20,63,1,'Oh tu es CE Nakai. Je lisais justement ton dossier ce matin.',22,0,0);
INSERT INTO seqtext VALUES(20,64,1,'Une certaine forme d''arythmie chronique et des carences congnitales du muscle cardiaque, c''est a ?',22,0,0);
INSERT INTO seqtext VALUES(20,65,1,'Il me fait signe de m''asseoir dans un fauteuil libre en face de son bureau.',1,0,0);
INSERT INTO seqtext VALUES(20,66,1,'Eh, oui.',12,0,0);
INSERT INTO seqtext VALUES(20,67,3,'nurse',305,400,10);
INSERT INTO seqtext VALUES(20,67,1,'Bon. Eh bien, tu as probablement t suffisamment inform sur l''cole, donc je vais tre rapide.',22,0,0);
INSERT INTO seqtext VALUES(20,68,1,'Nous avons toutes sortes d''installations disponibles, la plupart pour la thrapie physique, entre autres.',22,0,0);
INSERT INTO seqtext VALUES(20,69,1,'Il y a toujours quelqu''un de mon quipe pas loin, mme la nuit, alors n''hsite pas  nous appeler en cas de problme.',22,0,0);
INSERT INTO seqtext VALUES(20,70,1,'La fameuse quipe d''infirmiers 24/24.',1,0,0);
INSERT INTO seqtext VALUES(20,71,1,'Woaw, c''est comme un hpital.',12,0,0);
INSERT INTO seqtext VALUES(20,72,1,'Eh bien, pas exactement. Par exemple, nous ne faisons pas de chirurgie du cerveau ici.',22,0,0);
INSERT INTO seqtext VALUES(20,73,1,'Son rire semble tellement hors de propos que j''en reste  me demander pourquoi il a dit a.',1,0,0);
INSERT INTO seqtext VALUES(20,74,1,'Ouais... juste que c''est vraiment bizarre d''avoir autant de personnel mdical dans une cole.',12,0,0);
INSERT INTO seqtext VALUES(20,75,1,'Tu vas t''y habituer.',22,0,0);
INSERT INTO seqtext VALUES(20,76,1,'Je n''en suis pas aussi sr moi-mme, mais je vais laisser l''infirmier le croire.',1,0,0);
INSERT INTO seqtext VALUES(20,77,1,'Maintenant, laisse-moi juste le temps de trouver ton dossier  nouveau...',22,0,0);
INSERT INTO seqtext VALUES(20,78,1,'Pendant qu''il cherche quelque chose sur son ordinateur et dans les piles de papiers mlanges autour, je laisse mon regard errer dans la pice.',1,0,0);
INSERT INTO seqtext VALUES(20,79,1,'C''est la quintessence de l''ordinaire, pourrais-je dire.',1,0,0);
INSERT INTO seqtext VALUES(20,80,1,'Murs et plafond beiges, revtement de sol stratifi gris fonc, et tous les quipements qu''on pourrait attendre d''un bureau d''infirmier scolaire.',1,0,0);
INSERT INTO seqtext VALUES(20,81,1,'Mme les ridicules posters ducatifs sont accrochs sur les quatre murs, me rappelant de manger correctement - trois fois par jour et de tous les groupes alimentaires.',1,0,0);
INSERT INTO seqtext VALUES(20,82,3,'nurse',206,400,10);
INSERT INTO seqtext VALUES(20,82,1,'Souriant, l''infirmier tire un pais dossier  partir d''une pile de dossiers de mme paisseur et l''ouvre.',1,0,0);
INSERT INTO seqtext VALUES(20,83,1,'Alors, tu as dj des mdicaments contre l''arythmie, n''oublie pas de prendre tes pilules chaque matin et chaque soir ou a ne servira pas  grand-chose.',22,0,0);
INSERT INTO seqtext VALUES(20,84,3,'nurse',461,400,10);
INSERT INTO seqtext VALUES(20,84,1,'Cela mis  part... tu fais du sport ? Violent du genre... je sais pas, la boxe ?',22,0,0);
INSERT INTO seqtext VALUES(20,85,1,'Il sourit  sa propre plaisanterie, mais pas moi.',1,0,0);
INSERT INTO seqtext VALUES(20,86,1,'Euh, en fait, j''ai jou au foot de temps en temps avec quelques camarades de classe.',12,0,0);
INSERT INTO seqtext VALUES(20,87,1,'Bon, je crains de devoir te recommander de t''abstenir de faire a. Au moins pour le moment.',22,0,0);
INSERT INTO seqtext VALUES(20,88,1,'Oh.',12,0,0);
INSERT INTO seqtext VALUES(20,89,1,'Mon manque de raction lui fait lever un sourcil, mais honntement, je suis pas trop drang par son interdiction de frapper dans un ballon.',1,0,0);
INSERT INTO seqtext VALUES(20,90,1,'Je suppose que je n''ai jamais fait a par passion brlante pour le sport. Juste pour avoir quelque chose  faire.',1,0,0);
INSERT INTO seqtext VALUES(20,91,1,'Tout type de commotion crbrale peut tre trs dangereuse pour ton cur et prendre le risque d''avoir une autre attaque n''est pas une bonne ide.',22,0,0);
INSERT INTO seqtext VALUES(20,92,1,'C''tait la raison de ta premire attaque ? Il n''y a aucune mention de la cause dans tes papiers.',22,0,0);
INSERT INTO seqtext VALUES(20,93,1,'Euh... pas exactement.',12,0,0);
INSERT INTO seqtext VALUES(20,94,3,'nurse',353,400,10);
INSERT INTO seqtext VALUES(20,94,1,'J''vite la question de manire acceptable, et il lance un regard sur ses papiers, avec une expression plus srieuse sur son visage.',1,0,0);
INSERT INTO seqtext VALUES(20,95,1,'Pourtant, tu as besoin de garder ton corps en bonne sant donc faire un peu d''exercice te ferait du bien.',22,0,0);
INSERT INTO seqtext VALUES(20,96,1,'Nous avons la thrapie physique qui est disponible entre autres, comme je l''ai dit, mais je ne pense pas que tu aies besoin de quelque chose d''aussi lourd.',22,0,0);
INSERT INTO seqtext VALUES(20,97,1,'Juste quelques petits exercices rgulirement.',22,0,0);
INSERT INTO seqtext VALUES(20,98,1,'De la marche rapide ou mme un peu de jogging, du saut  la corde, ce genre de choses. La natation peut-tre ? Il y a une piscine ici.',22,0,0);
INSERT INTO seqtext VALUES(20,99,1,'On me l''a dj dit.',12,0,0);
INSERT INTO seqtext VALUES(20,100,3,'nurse',305,400,10);
INSERT INTO seqtext VALUES(20,100,1,'Tu savais ? Parfait.',22,0,0);
INSERT INTO seqtext VALUES(20,101,1,'En tout cas, et je suis sr qu''on t''en a dit autant, tu as juste besoin de faire attention  ne pas trop pousser.',22,0,0);
INSERT INTO seqtext VALUES(20,102,1,'Il agite son doigt pour souligner ce point. Ce n''est pas trs utile, j''ai dj entendu a des centaines de fois.',1,0,0);
INSERT INTO seqtext VALUES(20,103,3,'nurse',353,400,10);
INSERT INTO seqtext VALUES(20,103,1,'Absolument pas de risques inutiles. Prends soin de toi.',22,0,0);
INSERT INTO seqtext VALUES(20,104,1,'D''accord.',12,0,0);
INSERT INTO seqtext VALUES(20,105,1,'Il retourne dans mes papiers une fois de plus, et les met sur le bureau, apparemment content.',1,0,0);
INSERT INTO seqtext VALUES(20,106,3,'nurse',305,400,10);
INSERT INTO seqtext VALUES(20,106,1,'Bien. C''est tout, alors. Reviens me voir si tu as besoin de quelque chose.',22,0,0);
INSERT INTO seqtext VALUES(20,107,2,'',117,0,0);
INSERT INTO seqtext VALUES(20,108,1,'Je suis ressorti avant mme que je le ralise. Une visite rapide, en effet.',1,0,0);
INSERT INTO seqtext VALUES(21,1,2,'',186,0,0);
INSERT INTO seqtext VALUES(21,2,1,'Je me retrouve debout devant le btiment principal et l''auxiliaire, mme si  mes yeux, ils semblent toujours tre un seul et mme.',1,0,0);
INSERT INTO seqtext VALUES(21,3,1,'C''est le premier vrai regard que j''ai sur les autres tudiants, alors je regarde les gens sortir de l''cole, en train de se diriger vers le portail ou les dortoirs.',1,0,0);
INSERT INTO seqtext VALUES(21,4,1,'Chacun semble savoir o il doit aller.',1,0,0);
INSERT INTO seqtext VALUES(21,5,1,'Et je continue de penser que la plupart d''entre eux n''ont pas l''air spcial pour des tudiants d''une cole spciale. Et puis d''abord, moi non plus.',1,0,0);
INSERT INTO seqtext VALUES(21,6,1,'Est-ce que a fait de moi l''un d''entre eux ?{w} L''un d''entre nous ?',1,0,0);
INSERT INTO seqtext VALUES(21,7,1,'',1,0,0);
INSERT INTO seqtext VALUES(21,8,1,'Je devrais aller quelque part aussi, pour viter de me perdre.',1,0,0);
INSERT INTO seqtext VALUES(21,9,1,'Il est presque l''heure de dner, mais je me sens plus fatigu qu''affam.',1,0,0);
INSERT INTO seqtext VALUES(21,10,1,'La lassitude en moi me fait lentement avancer vers le dortoir, un peu  l''cart du complexe principal.',1,0,0);
INSERT INTO seqtext VALUES(21,11,2,'',189,0,0);
INSERT INTO seqtext VALUES(21,12,1,'Il y a une sorte de jardin entre l''cole et le dortoir; arbustes, fleurs et cette odeur omniprsente de l''herbe frachement coupe qui emplit l''atmosphre.',1,0,0);
INSERT INTO seqtext VALUES(21,13,1,'Dans la fatigue, il me vient  l''esprit que l''odeur me parat aussi forte parce que je n''tais pas du tout sorti  l''extrieur depuis trs longtemps.',1,0,0);
INSERT INTO seqtext VALUES(21,14,2,'',604,0,0);
INSERT INTO seqtext VALUES(21,15,1,'Le btiment du dortoir est grand et fait de briques rouges, a semble bien trop pompeux pour ce que c''est, mais j''avance, me rendant  l''intrieur.',1,0,0);
INSERT INTO seqtext VALUES(21,16,2,'',527,0,0);
INSERT INTO seqtext VALUES(21,17,1,'Je prends plus de temps que ncessaire pour repcher la cl que j''avais mise dans ma poche.',1,0,0);
INSERT INTO seqtext VALUES(21,18,1,'Chambre cent dix-neuf...',12,0,0);
INSERT INTO seqtext VALUES(21,19,1,'Contrairement  son apparence extrieure, l''intrieur du dortoir est plutt rcent, fonctionnel, et quelconque.',1,0,0);
INSERT INTO seqtext VALUES(21,20,1,'Tout comme le btiment principal, les halls et les portes sont larges pour les fauteuils roulants.',1,0,0);
INSERT INTO seqtext VALUES(21,21,1,'Je passe ma tte par l''ouverture de la porte de la salle commune.',1,0,0);
INSERT INTO seqtext VALUES(21,22,1,' l''intrieur quelques tudiants regardent la tlvision.',1,0,0);
INSERT INTO seqtext VALUES(21,23,1,'Un hochement de tte et un rapide salut avant de retourner  la tl.',1,0,0);
INSERT INTO seqtext VALUES(21,24,1,'On dirait que seules les filles sont sociables par ici. Je suppose que a me convient parfaitement.',1,0,0);
INSERT INTO seqtext VALUES(21,25,1,'Je monte l''escalier vers l''tage suprieur.',1,0,0);
INSERT INTO seqtext VALUES(21,26,2,'',534,0,0);
INSERT INTO seqtext VALUES(21,27,1,'Ici, des petits couloirs embranchent du couloir principal.',1,0,0);
INSERT INTO seqtext VALUES(21,28,1,'Chacun de ces halls secondaires semble avoir des toilettes et douches, ainsi que quatre chambres.',1,0,0);
INSERT INTO seqtext VALUES(21,29,1,' mi-chemin dans le hall, je trouve la chambre 119.',1,0,0);
INSERT INTO seqtext VALUES(21,30,1,'Il n''y a pas de noms sur les plaques des chambres adjacentes  la mienne. J''imagine que nous sommes juste deux ici.',1,0,0);
INSERT INTO seqtext VALUES(21,31,1,'La lumire brille sous la porte de la chambre 117, alors je frappe lgrement.',1,0,0);
INSERT INTO seqtext VALUES(21,32,1,'Bonjour, il y a quelqu''un ?',12,0,0);
INSERT INTO seqtext VALUES(21,33,1,'De l''intrieur, j''entends un peu de mouvement, puis le cliquetis de plus de serrures que je ne pensais que ces portes avaient. Aprs un moment, la porte s''ouvre en grinant.',1,0,0);
INSERT INTO seqtext VALUES(21,34,3,'kenji',351,400,10);
INSERT INTO seqtext VALUES(21,34,1,'Un garon  lunettes est debout dans l''embrasure. Il me regarde trs attentivement  travers ses trs paisses lunettes.',1,0,0);
INSERT INTO seqtext VALUES(21,35,1,'Qui est-ce ?',36,0,0);
INSERT INTO seqtext VALUES(21,36,1,'Aveugle ? Non, du moins pas compltement, pourquoi aurait-il des lunettes s''il l''tait?',1,0,0);
INSERT INTO seqtext VALUES(21,37,3,'kenji',133,400,10);
INSERT INTO seqtext VALUES(21,37,1,'Il se penche plus prs de moi jusqu'' ce que nos nez se touchent presque. Son haleine pue l''ail.',1,0,0);
INSERT INTO seqtext VALUES(21,38,1,'Hisao Nakai... J''emmnage dans la chambre d'' ct. J''ai pens que je devrais me prsenter...',12,0,0);
INSERT INTO seqtext VALUES(21,39,3,'kenji',482,400,10);
INSERT INTO seqtext VALUES(21,39,1,'Son visage s''illumine tout  coup en ralisant, et il se tient debout revenant  une position normale, tendant sa main dans un salut souriant, presque directement  mon diaphragme.',1,0,0);
INSERT INTO seqtext VALUES(21,40,1,'Oh, salut mec, j''suis Kenji.',36,0,0);
INSERT INTO seqtext VALUES(21,41,1,'Ah, salut.',12,0,0);
INSERT INTO seqtext VALUES(21,42,1,'Je prends la main moite de Kenji et la serre, toujours un peu branl par le brusque changement d''attitude et son salut vhment.',1,0,0);
INSERT INTO seqtext VALUES(21,43,1,'Il y avait quelques personnes louches entrant et sortant de ta chambre plus tt.',20,0,0);
INSERT INTO seqtext VALUES(21,44,1,'C''tait probablement mes parents.',12,0,0);
INSERT INTO seqtext VALUES(21,45,3,'kenji',351,400,10);
INSERT INTO seqtext VALUES(21,45,1,'Tes parents ? T''es sr ? Parce qu''il pouvait y avoir d''autres personnes, aussi. Tu ne peux pas juger un livre  sa couverture.',20,0,0);
INSERT INTO seqtext VALUES(21,46,1,'Son proverbe, mal plac, est laiss maladroitement en suspens entre nous alors que j''essaye de trouver une autre faon de rpondre.',1,0,0);
INSERT INTO seqtext VALUES(21,47,1,'Je dirais que les chances sont assez leves.',12,0,0);
INSERT INTO seqtext VALUES(21,48,3,'kenji',482,400,10);
INSERT INTO seqtext VALUES(21,48,1,'Il frmit et fait quelques gestes exagrs.',1,0,0);
INSERT INTO seqtext VALUES(21,49,1,'T''es un brave mec, Hisao.',20,0,0);
INSERT INTO seqtext VALUES(21,50,1,'Moi, je ne pense pas que je pourrais avoir confiance dans la chance.',20,0,0);
INSERT INTO seqtext VALUES(21,51,3,'kenji',192,400,10);
