INSERT INTO seqtext VALUES(21,52,1,'Est-ce que a veut dire que je ne devrais pas te faire confiance non plus ?',12,0,0);
INSERT INTO seqtext VALUES(21,53,1,'Il rflchit  ce sujet pendant un certain temps.',1,0,0);
INSERT INTO seqtext VALUES(21,54,1,'Une sage dcision.',20,0,0);
INSERT INTO seqtext VALUES(21,55,3,'kenji',482,400,10);
INSERT INTO seqtext VALUES(21,55,1,'Mince, tu es plus intelligent que tu ne le parais.',20,0,0);
INSERT INTO seqtext VALUES(21,56,1,'Probablement.',20,0,0);
INSERT INTO seqtext VALUES(21,57,1,'De quoi t''as l''air ? Pas intelligent, j''espre.',20,0,0);
INSERT INTO seqtext VALUES(21,58,3,'kenji',162,400,10);
INSERT INTO seqtext VALUES(21,58,1,'Il plisse les yeux et se penche plus prs encore, mais je me penche en arrire pour l''esquiver.',1,0,0);
INSERT INTO seqtext VALUES(21,59,3,'kenji',351,400,10);
INSERT INTO seqtext VALUES(21,59,1,'Tant pis, c''est pas grave.',20,0,0);
INSERT INTO seqtext VALUES(21,60,4,'kenji',0,0,0);
INSERT INTO seqtext VALUES(21,61,1,'Aprs a, il se tourne, ttonne un moment  la recherche de la poigne de porte, et ferme la porte derrire lui.',1,0,0);
INSERT INTO seqtext VALUES(21,62,1,'Je glisse la cl dans la serrure de la porte marque 119.',1,0,0);
INSERT INTO seqtext VALUES(21,63,2,'',283,0,0);
INSERT INTO seqtext VALUES(21,64,1,'Sombres murs beiges, draps blancs, un bureau fait de bois lger. Des rideaux laids.',1,0,0);
INSERT INTO seqtext VALUES(21,65,1,'a n''est la chambre de personne; impersonnelle, comme ma chambre d''hpital l''tait.',1,0,0);
INSERT INTO seqtext VALUES(21,66,1,'Mes valises sont poses au pied du lit, ayant l''air plus vides qu''elles ne l''taient ce matin.',1,0,0);
INSERT INTO seqtext VALUES(21,67,1,'L''armoire est encore ouverte, remplie de mes vtements.',1,0,0);
INSERT INTO seqtext VALUES(21,68,1,'D''ailleurs, il semble qu''il y a un certain nombre d''uniformes scolaires, suspendus l aussi.',1,0,0);
INSERT INTO seqtext VALUES(21,69,1,'Une note est pingle sur la poche de l''une des chemises.',1,0,0);
INSERT INTO seqtext VALUES(21,70,5,'"Salut Hicchan. Nous avons dball tes affaires et fait ton lit.\nIls nous ont dit que si celles-ci ne vont pas, alors tu devras aller  l''accueil demain.\nSi tu as le moindre problme, tu peux toujours nous appeler.\n\nBisous, Maman et Papa."',0,0,0);
INSERT INTO seqtext VALUES(21,71,1,'Eh bien, au moins je n''aurais pas a m''inquieter  propos du dballage.',1,0,0);
INSERT INTO seqtext VALUES(21,72,1,'J''aurais limite espr devoir le faire, au moins j''aurais eu quelque chose  faire.',1,0,0);
INSERT INTO seqtext VALUES(21,73,1,'Il est encore trop tt.',1,0,0);
INSERT INTO seqtext VALUES(21,74,1,'Je pose la note sur le bureau et me couche sur le lit, me sentant vide.',1,0,0);
INSERT INTO seqtext VALUES(21,75,1,'tre allong ici me donne envie de lire quelque chose, mais je n''ai rien avec moi.',1,0,0);
INSERT INTO seqtext VALUES(21,76,1,'Je me demande si l''hpital m''a conditionn pour me donner envie de lire ds que je n''ai rien  faire.',1,0,0);
INSERT INTO seqtext VALUES(21,77,1,'L''envie m''angoisse jusqu'' ce que je me lve.',1,0,0);
INSERT INTO seqtext VALUES(21,78,1,'Peut-tre que c''est le stress ou un truc du genre.',1,0,0);
INSERT INTO seqtext VALUES(21,79,1,'J''tais vraiment nerveux avant de venir et pendant toute la journe aussi. Je le suis encore, je crois.',1,0,0);
INSERT INTO seqtext VALUES(21,80,1,'Merde, j''ai besoin de me distraire d''une manire ou d''une autre, a ne sera pas aussi anormal tout le temps.',1,0,0);
INSERT INTO seqtext VALUES(21,81,1,'Demain, j''irai emprunter des livres  la bibliothque.',1,0,0);
INSERT INTO seqtext VALUES(21,82,1,'Ouais, je vais faire a.',1,0,0);
INSERT INTO seqtext VALUES(21,83,1,'Mais pour le moment...',1,0,0);
INSERT INTO seqtext VALUES(21,84,1,'Les flacons de mdicaments bien aligns sur ma table de nuit attirent mon regard.',1,0,0);
INSERT INTO seqtext VALUES(21,85,1,'J''en choisis un et le secoue juste pour entendre le contenu, puis je lis l''tiquette de la pharmacie dessus.',1,0,0);
INSERT INTO seqtext VALUES(21,86,5,'"Hisao Nakai\n\nDeux comprims par jour pour rester en vie"',0,0,0);
