INSERT INTO seqtext VALUES(21,92,2,'',607,0,0);
INSERT INTO seqtext VALUES(21,93,1,'Il ne me semble pas devenir plus familier, pas mme aprs la tombe de l''obscurit et que de longues ombres s''tirent  travers ma chambre comme des doigts.',1,0,0);
