INSERT INTO seqtext VALUES(22,11,1,'Je regarde les bouteilles pendant un moment, dlibrant, jusqu''au moment o j''ouvre une bouteille, prends une pilule dans son flacon, et sors une tablette de son paquet d''aluminium.',1,0,0);
INSERT INTO seqtext VALUES(22,12,1,'Je les avale avec une gorge d''eau sans penser  la chimie.',1,0,0);
