INSERT INTO seqtext VALUES(22,15,1,'Mettre un nouvel uniforme donne l''impression de s''habiller avec les vtements de quelqu''un d''autre.',1,0,0);
INSERT INTO seqtext VALUES(22,16,1,'L''odeur artificielle de dtergent gnrique envahit mon nez, mais la sensation du coton frais contre mon dos est une chose agrable, une chose naturelle.',1,0,0);
INSERT INTO seqtext VALUES(22,17,1,'On se sent comme dans un uniforme scolaire, comme on le devrait. Ce n''est pas trs diffrent de ce que j''avais l''habitude de porter auparavant.',1,0,0);
