INSERT INTO seqtext VALUES(29,3,1,'Me promenant dans les alles troites, je regarde le dos des livres dans un ordre alatoire, en fais parfois glisser un pour lire le rsum, en le prenant avec moi si a me parat bien.',1,0,0);
