INSERT INTO seqtext VALUES(58,99,1,'Je soupire et me lve de ma chaise, essayant d''avoir l''air le plus provocateur possible, car a semble tre un gros problme et je ne veux pas le laisser filer aussi facilement. Je me sens idiot, comme un poisson-ballon.',1,0,0);
