INSERT INTO seqtext VALUES(58,112,3,'shizu',297,240,10);
INSERT INTO seqtext VALUES(58,112,1,'',17,0,0);
INSERT INTO seqtext VALUES(58,113,3,'misha',139,560,10);
INSERT INTO seqtext VALUES(58,113,1,'Hicchan, tu as deux mignonnes reprsentantes du conseil des tudiants qui ont essay de bien t''accueillir et sont mme alles jusqu'' te payer le djeuner. Tu devrais montrer ta gratitude en rejoignant le conseil de sorte que tu puisses au moins les aider.',18,0,0);
INSERT INTO seqtext VALUES(58,114,1,'Ouais, a serait super si tu pouvais juste classer quelques papiers pour nous, et... le festival approche, et on doit construire des stands pour les jeux et la nourriture, juste quelques-uns, donc si tu y tais, a serait beaucoup plus facile pour nous...',18,0,0);
