INSERT INTO seqtext VALUES(58,117,1,'Je vais essayer une fois. Pourquoi pas ?',1,0,0);
INSERT INTO seqtext VALUES(58,118,1,'Juste un essai.',1,0,0);
INSERT INTO seqtext VALUES(58,119,1,'D''accord, je vais faire un galop d''essai. a ne signifie pas que je rejoins, ou quoi que ce soit de dfinitif, juste que je vais vous aider un peu, et je verrai. Et a compte aussi pour le djeuner, je ne fais pas a parce que j''en ai envie.',12,0,0);
INSERT INTO seqtext VALUES(58,120,3,'misha',139,560,10);
INSERT INTO seqtext VALUES(58,120,3,'shizu',455,240,10);
INSERT INTO seqtext VALUES(58,120,1,'Je finis la dernire bouche de mon sandwich, ce qui permet  Misha de remarquer pour la premire fois qu''elle a un parfait. Elle commence  taper dedans, au grand dsespoir de Shizune, je peux deviner  la faon dont elle joue avec ses doigts qu''elle a beaucoup  dire.',1,0,0);
INSERT INTO seqtext VALUES(58,121,3,'shizu',266,240,10);
INSERT INTO seqtext VALUES(58,121,1,'Chaque fois que je regarde Shizune, elle me regarde en retour avec un regard fixe et intense. Cette fois ne fait pas exception. Son visage est sans expression ; peut-tre qu''elle rflchit. Finalement, il cde la place  un sourire.',1,0,0);
