INSERT INTO seqtext VALUES(58,135,4,'shizu',0,0,0);
INSERT INTO seqtext VALUES(58,136,1,'Je ne comprends pas vraiment. Mais a me donne envie de savoir. Peut-tre que je devrais essayer d''apprendre le langue des signes. Cette cole doit avoir des cours pour a.',1,0,0);
