INSERT INTO seqtext VALUES(58,152,3,'misha',26,560,10);
INSERT INTO seqtext VALUES(58,152,1,'Hicchan, nous emmenions juste un ventuel futur membre du conseil des tudiants pour essayer de le recruter. C''est les affaires, les affaires~ ! Mais~ ! Tu n''es pas un membre du conseil, donc il n''y a aucune excuse !',18,0,0);
