INSERT INTO seqtext VALUES(58,158,3,'misha',139,560,10);
INSERT INTO seqtext VALUES(58,158,1,'Hicchan, si seulement tu tais un membre du conseil des tudiants... Mais~ ! Tu peux toujours le rejoindre maintenant, Hicchan ! Tu n''auras pas du tout d''ennuis, et nous serions en mesure de faire des super excursions comme a tout le temps ! a serait fun~ !',18,0,0);
INSERT INTO seqtext VALUES(58,159,1,'Je commence  penser que peut-tre, tout ceci n''tait qu''un plan complexe qui a men  ce moment. Peut-tre que Shizune m''a attir ici, en misant sur la possibilit que je perde la notion du temps et que je sois bloqu dans cette situation.',1,0,0);
INSERT INTO seqtext VALUES(58,160,1,'Diabolique. ...Eh bien, compte tenu de ce que je sais d''elle, j''aurais d tre plus sur mes gardes. Mais admettre que je me suis fait avoir, mme lgrement, serait impardonnable.',1,0,0);
