INSERT INTO seqtext VALUES(62,67,1,'Plus loin.',15,0,0);
INSERT INTO seqtext VALUES(62,68,4,'rin',0,0,0);
INSERT INTO seqtext VALUES(62,69,1,'Et avec a, elle part dans le couloir, la peinture et moi suivons car il n''y a gure d''autre choix pour nous deux.',1,0,0);
INSERT INTO seqtext VALUES(62,70,2,'',55,0,0);
