INSERT INTO seqtext VALUES(62,72,2,'',157,0,0);
INSERT INTO seqtext VALUES(62,73,1,'Tous les dix, quinze ou vingt pas je dois changer le bidon d''une main  l''autre parce que la fine poigne me coupe les doigts. Au moins, a empche mes bras de fatiguer trop vite.',1,0,0);
INSERT INTO seqtext VALUES(62,74,1,'Rin marche  ct de moi avec un rythme ingal que j''ai peine  copier, ou peut-tre que j''avance trangement  cause du poids supplmentaire.',1,0,0);
INSERT INTO seqtext VALUES(62,75,1,'Il semble que l''un de nous avance constamment trop lentement ou trop rapidement, et je n''arrive pas  savoir qui.',1,0,0);
INSERT INTO seqtext VALUES(62,76,2,'',609,0,0);
INSERT INTO seqtext VALUES(62,77,1,'Deux tages en dessous, les problmes apparaissent sous la forme de l''infirmier en chef et de son sourire narquois.',1,0,0);
INSERT INTO seqtext VALUES(62,78,3,'nurse',206,400,10);
