INSERT INTO seqtext VALUES(62,79,3,'rin',518,560,10);
INSERT INTO seqtext VALUES(62,79,1,'Il fait un hochement de tte courtois vers Rin qui ne le rend pas en retour, et se tourne alors vers moi, parce qu''il est vident que c''est avec moi qu''il doit parler.',1,0,0);
INSERT INTO seqtext VALUES(62,80,3,'nurse',353,240,10);
