INSERT INTO seqtext VALUES(62,81,1,'Je hoche la tte et attends, impassible, parce que je ne peux pas deviner ce qu''il a oubli. Le sentiment de subir une fouille interne ne me rend pas enthousiaste face  cette interruption, non plus.',1,0,0);
INSERT INTO seqtext VALUES(62,82,1,'Il s''agit de vos mdicaments.',22,0,0);
INSERT INTO seqtext VALUES(62,83,1,'Vu que vous ne prenez pas vos mdicaments actuels depuis longtemps, il pourrait y avoir certains effets secondaires imprvus, qui pourraient ncessiter une rvaluation du dosage ou mme de changer pour un autre type de mdicament.',22,0,0);
INSERT INTO seqtext VALUES(62,84,3,'nurse',305,240,10);
INSERT INTO seqtext VALUES(62,84,1,'Donc nous allons faire quelques tests rgulirement, mais ce que je veux c''est garder un il sur tout ce qui concerne votre tat, si vous voyez ce que je veux dire.',22,0,0);
