INSERT INTO seqtext VALUES(62,87,3,'rin',518,560,10);
INSERT INTO seqtext VALUES(62,87,1,'Donc comment allez-vous ? Tout va bien ?',22,0,0);
INSERT INTO seqtext VALUES(62,88,1,'J''abandonne et dpose le bidon sur le plancher avant de lui rpondre. Apparemment, a dure plus longtemps que ce que mes biceps peuvent grer.',1,0,0);
INSERT INTO seqtext VALUES(62,89,1,'Je suis sur le point de donner une rponse gnrale, mais je ralise  quel point je l''ai fait rcemment.',1,0,0);
INSERT INTO seqtext VALUES(62,90,1,'D''autres personnes m''ont demand a aussi. Enseignants et lves ici. Mes parents, les visiteurs, les infirmires, les mdecins de l''hpital.',1,0,0);
INSERT INTO seqtext VALUES(62,91,1,'Tout le monde semble s''en proccuper. C''est naturel pour un hpital, mais pas tellement pour une cole.{w} Sauf cette cole.',1,0,0);
INSERT INTO seqtext VALUES(62,92,1,'Il s''agit d''une petite cole, et les dortoirs des tudiants et la facult semblent tre trs serrs.',1,0,0);
INSERT INTO seqtext VALUES(62,93,1,'Du moins, c''est le sentiment que j''ai.',1,0,0);
INSERT INTO seqtext VALUES(62,94,1,'Et ce n''est pas le genre d''cole qui a souvent des tudiants transfrs.',1,0,0);
INSERT INTO seqtext VALUES(62,95,3,'rin',588,560,10);
INSERT INTO seqtext VALUES(62,95,1,'Cette pense me donne un frisson dans le dos, mais je donne une rponse gnrale quand mme.',1,0,0);
INSERT INTO seqtext VALUES(62,96,1,'',1,0,0);
