INSERT INTO seqtext VALUES(62,98,3,'nurse',461,240,10);
INSERT INTO seqtext VALUES(62,98,1,'Mes sources me disent que vous n''tes pas all  la piste d''athltisme ni mme  la piscine, donc je voudrais savoir si vous avez fait de l''exercice comme je l''ai demand.',22,0,0);
INSERT INTO seqtext VALUES(62,99,1,'Bien sr que je ne l''ai pas fait, mais sa faon de s''informer me donne le sentiment que j''aurais d me bouger les fesses et courir sur la piste depuis le tout premier jour.',1,0,0);
