INSERT INTO seqtext VALUES(62,101,3,'rin',518,560,10);
INSERT INTO seqtext VALUES(62,101,1,'Pas tant que a. Je connais juste quelques personnes. Mais ce n''est pas la question, donc n''essayez pas de vous dfiler.',22,0,0);
INSERT INTO seqtext VALUES(62,102,3,'rin',588,560,10);
INSERT INTO seqtext VALUES(62,102,1,'Eh bien, j''tais  l''instant en train de faire un exercice improvis de levage de poids.',12,0,0);
INSERT INTO seqtext VALUES(62,103,1,'Je ramasse et soulve le bidon plusieurs fois comme une triste imitation d''un bodybuilder, mme si a pse douloureusement sur mes bras.',1,0,0);
