INSERT INTO seqtext VALUES(62,106,3,'rin',518,1200,10);
INSERT INTO seqtext VALUES(62,106,1,'L''infirmier m''attrape par l''paule sans attendre la permission de Rin dont il n''avait pas besoin en premier lieu, et m''entrane de ct.',1,0,0);
INSERT INTO seqtext VALUES(62,107,3,'nurse',6,400,10);
INSERT INTO seqtext VALUES(62,107,1,'Quand je vous ai dit de faire de l''exercice, je ne plaisantais pas.',22,0,0);
INSERT INTO seqtext VALUES(62,108,1,'Je comprends que ce soit encore votre premire semaine et tout le reste, mais n''ignorez pas l''importance de ceci s''il vous plat.',22,0,0);
INSERT INTO seqtext VALUES(62,109,1,'La raison pour laquelle je suis si dur avec vous est que ces habitudes ne sont pas faciles  prendre.',22,0,0);
