INSERT INTO seqtext VALUES(64,1,1,'Ouais, je promets. Absolument.',12,0,0);
INSERT INTO seqtext VALUES(65,1,1,'Peut-tre, non... Je veux dire...',12,0,0);
INSERT INTO seqtext VALUES(65,2,1,'Il me lance une sorte de regard dsagrable quand je dis a, essayant de me faire retirer mes paroles.',1,0,0);
INSERT INTO seqtext VALUES(65,3,1,'Je veux dire, je sais pas... J''essaye toujours de m''habituer  cette cole.',12,0,0);
