INSERT INTO seqtext VALUES(65,5,1,'Vous n''tes pas trs convaincant, Hisao. Conseil numro un : les mdecins ne sont pas amuss si vous prenez leurs conseils  la lgre.',22,0,0);
INSERT INTO seqtext VALUES(65,6,1,'Qu''est-ce qui ne va pas avec lui ? Comme si un jour ou deux allaient faire une grande diffrence.',1,0,0);
