INSERT INTO seqtext VALUES(66,2,1,'D''accord. C''est mieux comme a.',22,0,0);
INSERT INTO seqtext VALUES(66,3,1,'Si vous allez sur la piste de l''cole demain matin, vous rencontrerez mon espion, qui n''aura probablement pas de problmes  vous offrir une sance si vous voulez courir un peu.',22,0,0);
