INSERT INTO seqtext VALUES(66,7,3,'rin',518,400,10);
INSERT INTO seqtext VALUES(66,7,1,'Il part avec un geste de la main et sans rponse, et je marche vers Rin qui a attendu, appuye nonchalamment contre le mur du couloir, regardant la ple lumire des nons au plafond.',1,0,0);
INSERT INTO seqtext VALUES(66,8,1,'Mme en m''approchant, elle ne les quitte pas des yeux.',1,0,0);
INSERT INTO seqtext VALUES(66,9,1,'Tu prends des mdicaments pour ton truc au cur ?',15,0,0);
INSERT INTO seqtext VALUES(66,10,1,'Tu coutais ?',12,0,0);
INSERT INTO seqtext VALUES(66,11,1,'C''est sorti plus accusateur que je ne le voulais, lui claquant accidentellement au visage.',1,0,0);
INSERT INTO seqtext VALUES(66,12,1,'Mais quand mme, je n''ai pas vraiment envie d''en parler. Je l''ai juste rencontre, je ne la connais pas. Ce ne sont pas ses affaires.',1,0,0);
INSERT INTO seqtext VALUES(66,13,1,'L''infirmier semblait ignorer la confidentialit aussi,  parler de ce genre de choses en public.',1,0,0);
INSERT INTO seqtext VALUES(66,14,1,'Mais ce n''est pas la faute de Rin, hein ?',1,0,0);
INSERT INTO seqtext VALUES(66,15,1,'Je lve les yeux vers elle, me sentant tout  coup un peu coupable, mais Rin est juste en train de regarder par-dessus mon paule bizarrement, la tte incline comme un oiseau.',1,0,0);
INSERT INTO seqtext VALUES(66,16,1,'Soupir.',1,0,0);
INSERT INTO seqtext VALUES(66,17,1,'Je ne sais pas pourquoi c''est si dur pour moi. J''ai l''impression qu''il y a un verrou inexplicable qui m''empche d''tre plus franc  propos de a.',1,0,0);
INSERT INTO seqtext VALUES(66,18,1,'...ouais. Ils sont pour mon cur.',12,0,0);
INSERT INTO seqtext VALUES(66,19,3,'rin',588,400,10);
INSERT INTO seqtext VALUES(66,19,1,'Ils vont te faire du bien ?',15,0,0);
INSERT INTO seqtext VALUES(66,20,1,'...non, pas vraiment.',12,0,0);
INSERT INTO seqtext VALUES(66,21,1,'Ils me font juste me sentir un peu moins mal.',12,0,0);
