INSERT INTO seqtext VALUES(89,24,1,'Je vais saisir l''opportunit de me rconcilier un peu avec Shizune, sans me faire enrler dans le conseil des tudiants encore une fois, quoique je suspecte la porte d''tre maintenant ferme pour moi.',1,0,0);
