INSERT INTO seqtext VALUES(91,7,1,'Hanako hoche la tte par -coups, toujours sur ses gardes, ses paules raides comme du bois. J''ai le sentiment qu''elle pourrait tre plus  l''aise toute seule aprs tout, mais il est trop tard pour faire marche arrire maintenant.',1,0,0);
