INSERT INTO seqtext VALUES(121,67,2,'',55,0,0);
INSERT INTO seqtext VALUES(121,68,1,'Rassur, je commence  me diriger vers la classe, mais le cours est presque  moiti termin, donc je commence  penser qu''il serait inutile de n''en faire que la moiti, et passe le reste du temps en buvant une canette dans le couloir.',1,0,0);
INSERT INTO seqtext VALUES(121,69,1,'Je garde un il sur la porte du conseil des tudiants, mais elle ne s''ouvre pas. Qu''est-ce qui leur prend autant de temps ? Elles sont occupes  faire ma part du travail ?',1,0,0);
