INSERT INTO seqtext VALUES(125,31,1,'Des tudiants se promnent en paires et en groupes autour du quadrilatre de la caftria.',1,0,0);
INSERT INTO seqtext VALUES(125,32,1,'Quelques camions de livraison arrivent  l''cole depuis l''picerie la plus proche.',1,0,0);
INSERT INTO seqtext VALUES(125,33,1,'Quelque part un chien de garde aboie sur un passant.',1,0,0);
INSERT INTO seqtext VALUES(125,34,1,'D''une certaine faon, quand je regarde vers le centre-ville, je pourais presque sentir le coeur de cette petite ville me toucher de faon presque palpable.',1,0,0);
INSERT INTO seqtext VALUES(125,35,1,'Le mode de vie trpidant des grandes mtropoles semble tellement loin d''ici ; les gens n''ont pas besoin de courir pour attraper un bus comme si leur vie en dpendait, ou d''avoir leurs sens surchargs par la lumire et les embouteillages.',1,0,0);
INSERT INTO seqtext VALUES(125,36,1,'Je me sens tonnamment optimiste  propos de ma nouvelle vie, regardant ma nouvelle ville, mme si elle ne sera mienne que pendant une courte anne.',1,0,0);
INSERT INTO seqtext VALUES(125,37,1,'Dcouvrir ma maladie et dmnager de chez moi a t si subit que je n''ai mme pas eu le temps de penser  la faon dont je le ressentais.',1,0,0);
INSERT INTO seqtext VALUES(125,38,1,'Quand je sors de l''ombre de la tour de l''horloge, je sens la chaleur toucher mon dos.',1,0,0);
INSERT INTO seqtext VALUES(125,39,1,'Le soleil brille dans un ciel crulen parfaitement clair.',1,0,0);
INSERT INTO seqtext VALUES(125,40,1,'Un lgre brise soufflant sur le toit me fait frissonner, mais seulement brivement.',1,0,0);
INSERT INTO seqtext VALUES(125,41,1,'Le vent porte l''odeur des arbres et des fleurs, pas la fume et l''odeur d''chappement des voitures comme autrefois, il y a juste quelques semaines de a.',1,0,0);
INSERT INTO seqtext VALUES(125,42,1,'Emi s''installe sur un banc avec Rin en remorque et sort un gros et deux petits paniers-repas de son sac.',1,0,0);
INSERT INTO seqtext VALUES(125,43,3,'rin',184,560,10);
INSERT INTO seqtext VALUES(125,43,3,'emi',20,240,10);
INSERT INTO seqtext VALUES(125,43,1,'Viens, Hisao ! Qu''est-ce que t''attends ?',14,0,0);
INSERT INTO seqtext VALUES(125,44,1,'Elle me fait signe de les rejoindre, me faisant de la place sur le banc dj petit.',1,0,0);
INSERT INTO seqtext VALUES(125,45,4,'emi',0,0,0);
INSERT INTO seqtext VALUES(125,46,4,'rin',0,0,0);
INSERT INTO seqtext VALUES(125,47,3,'bg',433,320,10);
INSERT INTO seqtext VALUES(125,47,3,'emi',422,200,10);
INSERT INTO seqtext VALUES(125,47,3,'rin',214,600,10);
INSERT INTO seqtext VALUES(125,47,3,'bg',433,400,10);
INSERT INTO seqtext VALUES(125,47,1,'Je m''assieds sur le bout du banc pour prendre le moins de place possible. C''est plutt troit, mais d''une faon, a nous convient bien.',1,0,0);
INSERT INTO seqtext VALUES(125,48,1,'Vue impressionnante.',12,0,0);
INSERT INTO seqtext VALUES(125,49,3,'emi',444,200,10);
INSERT INTO seqtext VALUES(125,49,1,'Emi retient un rire et place un panier-repas en face de Rin, et m''en tend un autre.',1,0,0);
INSERT INTO seqtext VALUES(125,50,3,'emi',321,200,10);
INSERT INTO seqtext VALUES(125,50,1,'Et voil ! Un djeuner, comme promis.',14,0,0);
INSERT INTO seqtext VALUES(125,51,1,'Fait maison, pas moins. Je suis impressionn.',1,0,0);
INSERT INTO seqtext VALUES(125,52,1,'Woaw. a a l''air vraiment bon.',12,0,0);
INSERT INTO seqtext VALUES(125,53,3,'emi',44,200,10);
INSERT INTO seqtext VALUES(125,53,1,'Merci ! Je les fais moi-mme quand j''peux.',14,0,0);
INSERT INTO seqtext VALUES(125,54,1,'La conversation s''teint ds que je commence  manger.',1,0,0);
INSERT INTO seqtext VALUES(125,55,3,'rin',226,600,10);
INSERT INTO seqtext VALUES(125,55,1,'Aprs quelques bouches, je relve la tte et remarque Rin ouvrant habilement sa bote et transportant un morceau de nourriture dans sa bouche en n''utilisant que ses pieds.',1,0,0);
INSERT INTO seqtext VALUES(125,56,1,'Mme si je l''ai dj vu avant, je ne peux rien faire d''autre qu''tre impressionn par sa dextrit.',1,0,0);
INSERT INTO seqtext VALUES(125,57,1,'a me rappelle aussi dans quel genre d''endroit je suis actuellement.',1,0,0);
INSERT INTO seqtext VALUES(125,58,1,'Je vais m''habituer un jour  voir des choses comme a ?',1,0,0);
INSERT INTO seqtext VALUES(125,59,1,'Je n''arrive pas  me dcider si tre habitu  quelque chose comme a serait une bonne ou une mauvaise chose.',1,0,0);
INSERT INTO seqtext VALUES(125,60,1,'Est-ce que m''habituer  cet endroit veut dire que je renonce  tre une personne normale ?',1,0,0);
INSERT INTO seqtext VALUES(125,61,1,'Ou a veut juste dire que je deviens plus comprhensif  propos des gens autour de moi ?',1,0,0);
INSERT INTO seqtext VALUES(125,62,1,'Je suis distrait de mes penses par la vue d''Emi dvorant son djeuner comme s''il avait insult ses anctres.',1,0,0);
INSERT INTO seqtext VALUES(125,63,3,'rin',347,600,10);
INSERT INTO seqtext VALUES(125,63,1,'T''as l''air d''avoir vraiment faim.',12,0,0);
INSERT INTO seqtext VALUES(125,64,3,'emi',144,200,10);
INSERT INTO seqtext VALUES(125,64,1,'Emi relve la tte, la bouche  moiti pleine, et avale avant de hocher la tte.',1,0,0);
INSERT INTO seqtext VALUES(125,65,1,'Ma course du matin m''ouvre toujours l''apptit.',14,0,0);
INSERT INTO seqtext VALUES(125,66,3,'emi',444,200,10);
INSERT INTO seqtext VALUES(125,66,1,'Ce qui est vrai, parce que je brle tout le djeuner plutt rapidement.',14,0,0);
INSERT INTO seqtext VALUES(125,67,3,'emi',321,200,10);
INSERT INTO seqtext VALUES(125,67,1,'Et m''aide  garder ma silhouette fminine.',14,0,0);
INSERT INTO seqtext VALUES(125,68,3,'rin',104,600,10);
INSERT INTO seqtext VALUES(125,68,1,'Il arriverait quoi si tu la perdais ? Tu deviendrais un homme ?',15,0,0);
INSERT INTO seqtext VALUES(125,69,1,'Je suis prs de m''touffer avec mon repas en essayant de ne pas rire.',1,0,0);
INSERT INTO seqtext VALUES(125,70,3,'emi',439,200,10);
INSERT INTO seqtext VALUES(125,70,1,'C''est une faon de parler.',14,0,0);
INSERT INTO seqtext VALUES(125,71,3,'rin',155,600,10);
INSERT INTO seqtext VALUES(125,71,1,'Et ta silhouette doit courir le matin aussi ?',15,0,0);
INSERT INTO seqtext VALUES(125,72,1,'Vous parlez toujours comme a ?',12,0,0);
INSERT INTO seqtext VALUES(125,73,3,'rin',359,600,10);
INSERT INTO seqtext VALUES(125,73,3,'emi',502,200,10);
INSERT INTO seqtext VALUES(125,73,6,'<span style="color: #b14343">Emi<br>Parler comme quoi ?</span><span style="color: #FF8D7C">Rin<br>Comme quoi ?</span>',0,0,0);
INSERT INTO seqtext VALUES(125,74,1,'Je pense que a rpond  ma question.',1,0,0);
INSERT INTO seqtext VALUES(125,75,1,'Euh, laissez tomber.',12,0,0);
INSERT INTO seqtext VALUES(125,76,1,'Donc, euh...',12,0,0);
INSERT INTO seqtext VALUES(125,77,1,'Je lutte pour trouver les mots, et pose la question vidente.',1,0,0);
INSERT INTO seqtext VALUES(125,78,1,'Comment vous vous tes rencontres ?',12,0,0);
INSERT INTO seqtext VALUES(125,79,3,'rin',226,600,10);
INSERT INTO seqtext VALUES(125,79,1,'Rin semble se contenter de laisser Emi rpondre  la question.',1,0,0);
INSERT INTO seqtext VALUES(125,80,3,'emi',144,200,10);
INSERT INTO seqtext VALUES(125,80,1,'Quelqu''un au dpartement du logement a pens qu''on se compltait bien l''une l''autre, donc on a t assignes  des chambres voisines.',14,0,0);
INSERT INTO seqtext VALUES(125,81,1,'Compltait l''une l''autre ?',12,0,0);
INSERT INTO seqtext VALUES(125,82,3,'rin',214,600,10);
INSERT INTO seqtext VALUES(125,82,1,'Comme des chaussures et un costume.',15,0,0);
INSERT INTO seqtext VALUES(125,83,1,'Hein ?',12,0,0);
INSERT INTO seqtext VALUES(125,84,3,'emi',413,200,10);
INSERT INTO seqtext VALUES(125,84,1,'Emi rit de ma confusion.',1,0,0);
INSERT INTO seqtext VALUES(125,85,1,'Mets-nous ensemble et on a tous nos membres, tu vois ?',14,0,0);
INSERT INTO seqtext VALUES(125,86,3,'emi',422,200,10);
INSERT INTO seqtext VALUES(125,86,1,'Donc j''ai commenc  aider Rin  se prparer le matin, et c''est tout !',14,0,0);
INSERT INTO seqtext VALUES(125,87,3,'emi',144,200,10);
INSERT INTO seqtext VALUES(125,87,1,'Je veux dire, tu ne peux pas aider quelqu''un  s''habiller tous les matins et ne pas t''entendre avec elle.',14,0,0);
INSERT INTO seqtext VALUES(125,88,1,'Je vois.',12,0,0);
INSERT INTO seqtext VALUES(125,89,1,'Rin choisit ce moment pour intervenir.',1,0,0);
INSERT INTO seqtext VALUES(125,90,3,'rin',104,600,10);
INSERT INTO seqtext VALUES(125,90,1,'J''ai du mal avec les chemises.',15,0,0);
INSERT INTO seqtext VALUES(125,91,1,'Oui, a semble... plutt vident.',12,0,0);
INSERT INTO seqtext VALUES(125,92,3,'rin',421,600,10);
INSERT INTO seqtext VALUES(125,92,1,'Vraiment ?',15,0,0);
INSERT INTO seqtext VALUES(125,93,1,'Un peu... ?',12,0,0);
INSERT INTO seqtext VALUES(125,94,1,'a n''aide pas, mais au moins Emi semble trouver a drle.',1,0,0);
INSERT INTO seqtext VALUES(125,95,1,'a, combin avec le fait que Rin est vraiment curieuse, a me fait me sentir mieux, et pourtant, confus.',1,0,0);
INSERT INTO seqtext VALUES(125,96,1,'Je veux dire, t''as pas de bras.',12,0,0);
INSERT INTO seqtext VALUES(125,97,1,'Donc, euh, mettre une chemise semble tre quelque chose qui serait... difficile.',12,0,0);
INSERT INTO seqtext VALUES(125,98,1,'Tu sais quoi ? Je vais juste me taire maintenant.',1,0,0);
INSERT INTO seqtext VALUES(125,99,1,'a va me sauver de bien des ennuis  long terme.',1,0,0);
INSERT INTO seqtext VALUES(125,100,1,'Rin hoche la tte avec ce que je suspecte tre de la sagesse.',1,0,0);
INSERT INTO seqtext VALUES(125,101,3,'rin',286,600,10);
INSERT INTO seqtext VALUES(125,101,1,'Je vois.',15,0,0);
INSERT INTO seqtext VALUES(125,102,3,'rin',347,600,10);
INSERT INTO seqtext VALUES(125,102,1,'La conversation prend fin ds que je dtourne mon attention sur mon djeuner.',1,0,0);
INSERT INTO seqtext VALUES(125,103,1,'C''est vraiment trs bon.',1,0,0);
INSERT INTO seqtext VALUES(125,104,1,'Emi finit son repas en premier et fait un bruit de satisfaction.',1,0,0);
INSERT INTO seqtext VALUES(125,105,3,'emi',495,200,10);
INSERT INTO seqtext VALUES(125,105,1,'Ah, c''tait bon.',14,0,0);
INSERT INTO seqtext VALUES(125,106,1,'Pendant qu''elle est occupe  essuyer les restes de son djeuner, Rin parle.',1,0,0);
INSERT INTO seqtext VALUES(125,107,3,'rin',104,600,10);
INSERT INTO seqtext VALUES(125,107,1,'J''ai soif.',15,0,0);
INSERT INTO seqtext VALUES(125,108,3,'emi',465,200,10);
INSERT INTO seqtext VALUES(125,108,1,'Oh ! J''ai presque oubli ! Dsole !',14,0,0);
INSERT INTO seqtext VALUES(125,109,3,'emi',413,200,10);
INSERT INTO seqtext VALUES(125,109,1,'Avec un grand geste, elle attrape son sac et en sort trois briques de jus de fruit.',1,0,0);
INSERT INTO seqtext VALUES(125,110,1,'Elle me lance ce qui semble tre du jus de canneberge, il y en a une pour Rin qui semble tre une sorte de lait  la fraise (compltement rose), et en garde une (tout aussi rose) aux fruits presss pour elle-mme.',1,0,0);
INSERT INTO seqtext VALUES(125,111,3,'rin',226,600,10);
INSERT INTO seqtext VALUES(125,111,1,'Rin plante adroitement sa paille dans le haut de sa brique et commence  boire.',1,0,0);
INSERT INTO seqtext VALUES(125,112,1,'Je suis encore une fois impressionn par sa souplesse, mais cette fois je garde mon commentaire pour moi.',1,0,0);
INSERT INTO seqtext VALUES(125,113,1,'D''une certaine faon, je ne crois pas que Emi ou Rin soit le genre de personnes  penser  deux fois  la faon dont elles se dbrouillent avec leurs handicaps particuliers.',1,0,0);
INSERT INTO seqtext VALUES(125,114,1,'Et spcialement Rin.',1,0,0);
INSERT INTO seqtext VALUES(125,115,1,'En effet, elle donne l''impression d''tre totalement ignorante du fait qu''il lui manque des membres.',1,0,0);
INSERT INTO seqtext VALUES(125,116,1,'Que ce soit ou non une dcision consciente, c''est une autre affaire.',1,0,0);
INSERT INTO seqtext VALUES(125,117,1,'Je ne suis franchement pas sr.',1,0,0);
INSERT INTO seqtext VALUES(125,118,3,'emi',144,200,10);
INSERT INTO seqtext VALUES(125,118,1,'Alors Hisao, comment tu te sens ici ?',14,0,0);
INSERT INTO seqtext VALUES(125,119,3,'rin',347,600,10);
INSERT INTO seqtext VALUES(125,119,1,'Hmm ?',12,0,0);
INSERT INTO seqtext VALUES(125,120,1,'C''est plutt bien, en fait. J''aime bien les endroits levs, pour la vue.',12,0,0);
INSERT INTO seqtext VALUES(125,121,1,'Merci de m''avoir invit ici.',12,0,0);
INSERT INTO seqtext VALUES(125,122,1,'Et pour le repas, aussi.',12,0,0);
INSERT INTO seqtext VALUES(125,123,3,'emi',44,200,10);
INSERT INTO seqtext VALUES(125,123,1,'Emi fait un sourire de mille watt, contente de ma rponse, je suppose.',1,0,0);
INSERT INTO seqtext VALUES(125,124,1,'Aucun problme !',14,0,0);
INSERT INTO seqtext VALUES(125,125,3,'emi',321,200,10);
INSERT INTO seqtext VALUES(125,125,1,'N''hsite pas  manger avec nous la prochaine fois aussi, d''accord ?',14,0,0);
INSERT INTO seqtext VALUES(125,126,1,'Je ne te ferai pas de djeuner, mais tu peux apporter le tien ici.',14,0,0);
INSERT INTO seqtext VALUES(125,127,1,'Pas de service djeuner ? Je sais pas...',12,0,0);
INSERT INTO seqtext VALUES(125,128,3,'emi',439,200,10);
INSERT INTO seqtext VALUES(125,128,1,'Emi affiche un air ironiquement offens.',1,0,0);
INSERT INTO seqtext VALUES(125,129,1,'Tu essayes de tirer avantage de ma gentillesse ?',14,0,0);
INSERT INTO seqtext VALUES(125,130,1,'Quel culot !',14,0,0);
INSERT INTO seqtext VALUES(125,131,3,'emi',413,200,10);
INSERT INTO seqtext VALUES(125,131,1,'Elle rit.',1,0,0);
INSERT INTO seqtext VALUES(125,132,3,'emi',508,200,10);
INSERT INTO seqtext VALUES(125,132,1,'Eh bien, si c''est ta rponse, je suppose que Rin et moi continuerons  manger toutes seules...',14,0,0);
INSERT INTO seqtext VALUES(125,133,1,'Je suis soudainement attaqu par le plus dchirant regard de chiot que je n''ai jamais vu sur le visage d''Emi.',1,0,0);
INSERT INTO seqtext VALUES(125,134,1,'Je rigole ! Je plaisantais !',12,0,0);
INSERT INTO seqtext VALUES(125,135,1,'J''adorerais manger encore mon djeuner ici.',12,0,0);
INSERT INTO seqtext VALUES(125,136,1,'Bon emplacement, et la compagnie est pas mal aussi.',12,0,0);
INSERT INTO seqtext VALUES(125,137,3,'emi',144,200,10);
INSERT INTO seqtext VALUES(125,137,1,'Emi fronce les sourcils  ma dclaration de pas mal mais semble plutt contente que j''aie accept son invitation.',1,0,0);
INSERT INTO seqtext VALUES(125,138,1,'J''imagine que a fait de nous des amis maintenant.',1,0,0);
INSERT INTO seqtext VALUES(125,139,1,'Ou du moins des connaissances.',1,0,0);
INSERT INTO seqtext VALUES(125,140,1,'La cloche sonne, signalant l''heure de redescendre.',1,0,0);
INSERT INTO seqtext VALUES(125,141,3,'emi',149,200,10);
INSERT INTO seqtext VALUES(125,141,1,'Rin, tu n''as pas fini ton repas encore une fois !',14,0,0);
INSERT INTO seqtext VALUES(125,142,3,'rin',104,600,10);
INSERT INTO seqtext VALUES(125,142,1,'Je n''avais pas si faim.',15,0,0);
INSERT INTO seqtext VALUES(125,143,3,'emi',439,200,10);
INSERT INTO seqtext VALUES(125,143,1,'Si tu ne manges pas plus, tu vas t''vanouir !',14,0,0);
INSERT INTO seqtext VALUES(125,144,3,'rin',207,600,10);
INSERT INTO seqtext VALUES(125,144,1,'Rin hausse les paules, comme si c''tait un risque acceptable.',1,0,0);
INSERT INTO seqtext VALUES(125,145,1,'Allez, on ferait mieux d''y aller.',12,0,0);
INSERT INTO seqtext VALUES(125,146,2,'',288,0,0);
INSERT INTO seqtext VALUES(125,147,1,'Nous partons dans les escaliers tous les trois.',1,0,0);
INSERT INTO seqtext VALUES(125,148,2,'',451,0,0);
INSERT INTO seqtext VALUES(125,149,1,'Les cours de l''aprs-midi passent. Encore une fois, je me retrouve avec aucun projet pour aprs l''cole, donc je me dirige vers la bibliothque pour rendre quelques livres que j''ai fini de lire.',1,0,0);
INSERT INTO seqtext VALUES(125,150,2,'',165,0,0);
INSERT INTO seqtext VALUES(125,151,1,'En rentrant, je vois qu''il y a  peu prs autant d''tudiants ici qu''il y en avait mardi, c''est d''autant presque vident avec le silence total qui enveloppe la pice.',1,0,0);
INSERT INTO seqtext VALUES(125,152,3,'yuuko',227,400,10);
INSERT INTO seqtext VALUES(125,152,1,'Pendant que je lche les livres que j''avais emprunts dans le casier de retour qui est sur le comptoir, Yuuko apparat soudainement derrire, surprise du bruit que a a fait quand ils ont heurt le casier  ct d''elle.',1,0,0);
INSERT INTO seqtext VALUES(125,153,1,'Ah, dsol Yuuko. Je ne voulais pas vous surprendre.',12,0,0);
INSERT INTO seqtext VALUES(125,154,3,'yuuko',576,400,10);
INSERT INTO seqtext VALUES(125,154,1,'Non, non. C''est bon. a arrive... souvent. Je suis habitue  a maintenant.',24,0,0);
INSERT INTO seqtext VALUES(125,155,3,'yuuko',500,400,10);
INSERT INTO seqtext VALUES(125,155,1,'Hum, je peux t''aider ?',24,0,0);
INSERT INTO seqtext VALUES(125,156,1,'C''est bon, je pense que je sais o tout se trouve. Merci quand mme.',12,0,0);
INSERT INTO seqtext VALUES(125,157,4,'yuuko',0,0,0);
INSERT INTO seqtext VALUES(125,158,1,'Je suppose que je vais emprunter un ou deux autres livres pendant que je suis l. Il n''y a pas grand-chose d''autre  faire, et aprs avoir lu autant durant mon sjour  l''hpital, c''est devenu une habitude dure  rompre.',1,0,0);
INSERT INTO seqtext VALUES(125,159,1,'Je me promne dans la section science-fiction  l''arrire de la bibliothque, scrutant les tagres pour quoi que ce soit qui me sauterait aux yeux.',1,0,0);
INSERT INTO seqtext VALUES(125,160,1,'Pendant ce temps, je regarde dans le coin o Hanako tait la dernire fois que je me trouvais ici, ne m''attendant pas  grand-chose.',1,0,0);
INSERT INTO seqtext VALUES(125,161,2,'',616,0,0);
INSERT INTO seqtext VALUES(125,162,1,'...tonnamment, pourtant, elle est l, compltement absorbe dans un livre assez pais. Je dcide de ne pas la gner comme la dernire fois et de retourner  ma recherche de livres  lire.',1,0,0);
INSERT INTO seqtext VALUES(125,163,2,'',302,0,0);
INSERT INTO seqtext VALUES(125,164,1,'Aprs une dure indiscernable de temps pass  parcourir les alles, je me dcide finalement pour deux livres  prendre et les sors de l''tagre.',1,0,0);
INSERT INTO seqtext VALUES(125,165,1,'Avec un minimum de tapage, je marche rapidement jusqu''au comptoir, fais enregistrer les livres et les mets dans mon sac pendant que je sors.',1,0,0);
INSERT INTO seqtext VALUES(125,166,2,'',53,0,0);
INSERT INTO seqtext VALUES(125,167,1,'Au moment o je sors du btiment principal, le soleil n''est pas encore totalement couch. Il reste quelques lves, mais la majorit sont partis ; sans doute dans leurs maisons et dortoirs.',1,0,0);
INSERT INTO seqtext VALUES(126,1,2,'',283,0,0);
INSERT INTO seqtext VALUES(126,2,1,'Me sentant compltement vide, je me dirige vers ma chambre pour lire les livres que j''ai emprunts. Il y a eu bien suffisamment d''action et d''excitation pour la journe.',1,0,0);
INSERT INTO seqtext VALUES(126,3,1,'Le premier est Alice au pays des merveilles. Je connais l''histoire, bien sr, mais je n''ai jamais lu le livre original.',1,0,0);
INSERT INTO seqtext VALUES(126,4,1,'C''est juste aussi fou que ce que je me rappelle de l''histoire, avec les personnages loufoques et l''intrigue absurde.',1,0,0);
INSERT INTO seqtext VALUES(126,5,1,'Je commence  penser que je suis moi aussi une sorte d''Alice, dgringolant, impuissant, dans le terrier du lapin dans ce pays imaginaire handicap.',1,0,0);
INSERT INTO seqtext VALUES(126,6,1,'...D''accord, c''est une expression un peu forte. Pourtant, l''endroit isol et la manire dont l''cole s''adapte  absolument tout sont inquitants. C''est comme un autre monde.',1,0,0);
INSERT INTO seqtext VALUES(126,7,1,'Je me demande pourquoi je ne peux pas branler le sentiment d''tre un tranger comme Alice, en dpit du fait que presque tout le monde est si accueillant et amical avec moi.',1,0,0);
INSERT INTO seqtext VALUES(126,8,1,'Tournant une autre page, mon esprit commence  driver loin du livre. C''est silencieux, je peux entendre le battement de mon cur heurter le tissu de ma chemise.',1,0,0);
INSERT INTO seqtext VALUES(126,9,1,'Pour je ne sais quelque raison, je me sens vraiment mal depuis ce qui s''est pass dans la fort avec Iwanako. Comme si j''tais enferm dans une cage avec quelque chose de mchant et effrayant.',1,0,0);
INSERT INTO seqtext VALUES(126,10,2,'',607,0,0);
INSERT INTO seqtext VALUES(126,11,1,'Je pose le livre pendant un moment et regarde le plafond, prenant mon temps pour me dbarrasser de ce sentiment.',1,0,0);
INSERT INTO seqtext VALUES(126,12,1,'200 pages plus tard, je m''endors.',1,0,0);
INSERT INTO seqtext VALUES(126,13,2,'',190,0,0);
INSERT INTO seqtext VALUES(127,1,2,'',53,0,0);
INSERT INTO seqtext VALUES(127,2,1,'Je suppose que je dois aller acheter des trucs. Je ne peux pas continuer  aller  la caftria ou aller manger dehors durant mon sjour ici.',12,0,0);
INSERT INTO seqtext VALUES(127,3,2,'',574,0,0);
INSERT INTO seqtext VALUES(127,4,1,'Pendant que je passe la porte, je fais quelques tirements pour essayer d''attnuer la fatigue qui s''est accumule au cours de la semaine.',1,0,0);
INSERT INTO seqtext VALUES(127,5,2,'',198,0,0);
INSERT INTO seqtext VALUES(127,6,3,'lilly',468,400,10);
INSERT INTO seqtext VALUES(127,6,1,'Aprs avoir travers et avoir tourn au coin, je vois un personnage solitaire marchant en descendant vers la petite ville plus loin. La couleur de ses cheveux et le bruit de sa canne ne trompent pas.',1,0,0);
INSERT INTO seqtext VALUES(127,7,3,'lilly',365,400,10);
INSERT INTO seqtext VALUES(127,7,1,'Je marche rapidement jusqu'' elle, ce qui semble attirer son attention sans qu''un mot ait besoin d''tre dit.',1,0,0);
INSERT INTO seqtext VALUES(127,8,1,'Salut, Lilly.',12,0,0);
INSERT INTO seqtext VALUES(127,9,3,'lilly',61,400,10);
INSERT INTO seqtext VALUES(127,9,1,'Elle prend un moment pour placer la voix, ajustant lgrement sa tte pour faire mieux face  la source vocale.',1,0,0);
INSERT INTO seqtext VALUES(127,10,3,'lilly',385,400,10);
INSERT INTO seqtext VALUES(127,10,1,'...Hisao ?',16,0,0);
INSERT INTO seqtext VALUES(127,11,1,'Yep, c''est moi.',12,0,0);
INSERT INTO seqtext VALUES(127,12,1,'Elle semble avoir une bonne mmoire pour les voix. Le fait qu''elle ait effectivement russi  s''en souvenir est une agrable surprise.',1,0,0);
INSERT INTO seqtext VALUES(127,13,1,'Bonsoir. Qu''est-ce qui t''amne ici ?',16,0,0);
INSERT INTO seqtext VALUES(127,14,3,'lilly',61,400,10);
INSERT INTO seqtext VALUES(127,14,1,'Encore une fois, elle se courbe poliment. Et, encore une fois, je fais de mme poliment avant de raliser que je n''ai pas  le faire.',1,0,0);
INSERT INTO seqtext VALUES(127,15,1,'Je vais juste en ville. Et toi ?',12,0,0);
INSERT INTO seqtext VALUES(127,16,3,'lilly',403,400,10);
INSERT INTO seqtext VALUES(127,16,1,'Eh bien, eh bien, quelle concidence.',16,0,0);
INSERT INTO seqtext VALUES(127,17,1,'On fait la mme chose, hein ?',12,0,0);
INSERT INTO seqtext VALUES(127,18,3,'lilly',385,400,10);
INSERT INTO seqtext VALUES(127,18,1,'Mmh. Je vais habituellement faire les courses le vendredi.',16,0,0);
INSERT INTO seqtext VALUES(127,19,3,'lilly',365,400,10);
INSERT INTO seqtext VALUES(127,19,1,'Elle fait une pause pendant un moment, ayant l''air soudainement un peu perdue.',1,0,0);
INSERT INTO seqtext VALUES(127,20,1,'Cela dit, Hanako vient habituellement en ville avec moi.',16,0,0);
INSERT INTO seqtext VALUES(127,21,1,'Ah. Pas perdue, mais inquite. Elles semblent tre trs proches toutes les deux. C''est un peu surprenant que Hanako puisse simplement oublier Lilly comme a.',1,0,0);
INSERT INTO seqtext VALUES(127,22,1,'Je l''ai remarque lisant  la bibliothque. Elle est probablement pige dans un livre.',12,0,0);
INSERT INTO seqtext VALUES(127,23,3,'lilly',61,400,10);
INSERT INTO seqtext VALUES(127,23,1,'Elle laisse chapper un petit soupir de soulagement.',1,0,0);
INSERT INTO seqtext VALUES(127,24,1,'Merci. Elle a l''habitude de faire a.',16,0,0);
INSERT INTO seqtext VALUES(127,25,1,'Lectrice avide ?',12,0,0);
INSERT INTO seqtext VALUES(127,26,3,'lilly',385,400,10);
INSERT INTO seqtext VALUES(127,26,1,'Exact. Elle n''aime pas tre avec beaucoup de gens, donc lire loin de tout le monde lui permet de se dtendre un peu.',16,0,0);
INSERT INTO seqtext VALUES(127,27,1,'Mme si elle n''en avait pas l''intention, je ne peux pas m''empcher de grimacer en me rappelant ma premire rencontre avec elle.',1,0,0);
INSERT INTO seqtext VALUES(127,28,3,'lilly',240,400,10);
INSERT INTO seqtext VALUES(127,28,1,'Ne voulant videmment pas en parler, je reste silencieux pendant que nous continuons sans un mot notre marche sur la route tranquille.',1,0,0);
INSERT INTO seqtext VALUES(127,29,1,'Tac, tac. Tac, tac.',1,0,0);
INSERT INTO seqtext VALUES(127,30,1,'Avec la route dpourvue de voitures et les tudiants de Yamaku de plus en plus loin derrire nous, le bruissement silencieux des feuilles et le tapotement mesur de la canne de Lilly contre le trottoir sont tout ce qui peut tre entendu.',1,0,0);
INSERT INTO seqtext VALUES(127,31,1,'C''est plutt pas mal, spcialement compar  l''agitation de l''endroit o j''avais l''habitude de vivre.',1,0,0);
INSERT INTO seqtext VALUES(127,32,1,'Avant que je m''en rende compte, je me suis tellement dtendu qu''un billement prolong s''chappe sans que je puisse le contrler.',1,0,0);
INSERT INTO seqtext VALUES(127,33,3,'lilly',200,400,10);
INSERT INTO seqtext VALUES(127,33,1,'Fatigu ?',16,0,0);
INSERT INTO seqtext VALUES(127,34,1,'Ouais, c''tait plutt la course ces derniers jours.',12,0,0);
INSERT INTO seqtext VALUES(127,35,1,'C''est un euphmisme, videmment. tre transfr dans une autre cole serait dj difficile, mais rien  ct de a...',1,0,0);
INSERT INTO seqtext VALUES(127,36,3,'lilly',385,400,10);
INSERT INTO seqtext VALUES(127,36,1,'Eh bien, heureusement tout devrait se ralentir pour toi. Le festival met tout le monde en vrille en ce moment, et tu es apparu en plein milieu.',16,0,0);
INSERT INTO seqtext VALUES(127,37,1,'Pendant un moment j''ai hsit, mais tant donn son apparente tolrance  la franchise, je dcide de lui dire le fond de ma pense.',1,0,0);
INSERT INTO seqtext VALUES(127,38,1,'J''imagine. Yamaku est plutt diffrente cependant. Je veux dire, les formalits entourant tout a, l''isolement du campus... sans mentionner la diffrence la plus vidente.',12,0,0);
INSERT INTO seqtext VALUES(127,39,1,'C''est un tat d''esprit compltement diffrent. Je suppose que je vais m''y habituer, avec le temps.',12,0,0);
INSERT INTO seqtext VALUES(127,40,3,'lilly',240,400,10);
INSERT INTO seqtext VALUES(127,40,1,'Elle fait un hochement de tte approbateur, apparemment satisfaite de ma rponse. Je me sens presque comme si elle m''avait inclu dans le troupeau d''lves qu''elle mne, avec la classe 3-2 et Hanako.',1,0,0);
INSERT INTO seqtext VALUES(127,41,1,'Bah, pas que a m''intresse. C''est bien de penser  autre chose qu'' ma poitrine en tout cas.',1,0,0);
INSERT INTO seqtext VALUES(127,42,3,'lilly',385,400,10);
INSERT INTO seqtext VALUES(127,42,1,'Regardant le bon ct des choses, on peut y voir la chance pour un nouveau commencement. Tu devrais chrir l''opportunit de te faire de nouveaux amis.',16,0,0);
INSERT INTO seqtext VALUES(127,43,1,'C''est optimiste. Nanmoins, c''est bon d''avoir une attitude positive  propos de telles choses, je suppose.',1,0,0);
INSERT INTO seqtext VALUES(127,44,1,'J''imagine que c''est une bonne faon de prendre les choses.',12,0,0);
INSERT INTO seqtext VALUES(127,45,2,'',362,0,0);
INSERT INTO seqtext VALUES(127,46,3,'lilly',368,400,10);
INSERT INTO seqtext VALUES(127,46,1,'Marchant sur la route descendante, elle semble tre devenue quelque peu instable. Avant que je puisse demander ce qui la perturbe, elle semble se ressaisir et parle d''autre chose.',1,0,0);
INSERT INTO seqtext VALUES(127,47,3,'lilly',61,400,10);
INSERT INTO seqtext VALUES(127,47,1,'Donc, tu vas o en ville ?',16,0,0);
INSERT INTO seqtext VALUES(127,48,1,'C''est effectivement une trs bonne question. Je suis venu pour acheter  manger, mais la disposition de l''endroit m''est encore trangre.',1,0,0);
INSERT INTO seqtext VALUES(127,49,1,'J''avais l''intention de me balader et voir ce que je pouvais trouver, mais avec le coucher de soleil approchant dj et la nuit qui n''est pas trop lointaine, a ne semble pas trs sage.',1,0,0);
INSERT INTO seqtext VALUES(127,50,1,'Je vais lui demander mon chemin. Encore.',1,0,0);
INSERT INTO seqtext VALUES(127,51,1,'Je vais juste acheter  manger... mais maintenant que tu en parles, je ne sais pas vraiment le chemin.',12,0,0);
INSERT INTO seqtext VALUES(127,52,3,'lilly',169,400,10);
INSERT INTO seqtext VALUES(127,52,1,'Eh bien, tu as de la chance. J''allais justement  la suprette moi-mme.',16,0,0);
INSERT INTO seqtext VALUES(127,53,1,'On dirait que je dois encore compter sur toi, alors. Merci.',12,0,0);
INSERT INTO seqtext VALUES(127,54,1,'Nous marchons ensemble vers le magasin, mon rythme soigneusement ralenti pour rester  ct d''elle. Compar  mon rythme de pas habituel, le sien est un peu plus lent. Non pas que ce soit sans raison.',1,0,0);
INSERT INTO seqtext VALUES(127,55,2,'',212,0,0);
INSERT INTO seqtext VALUES(127,56,1,'Aprs ce qui ne pourrait pas tre plus que quelques minutes, j''ai l''objectif dans ma ligne de mire. Cette ville est vraiment trs petite.',1,0,0);
INSERT INTO seqtext VALUES(127,57,2,'',346,0,0);
INSERT INTO seqtext VALUES(127,58,1,'Sans plus attendre, nous rentrons  l''intrieur avec des salutations depuis le comptoir.',1,0,0);
INSERT INTO seqtext VALUES(127,59,3,'lilly',497,400,10);
INSERT INTO seqtext VALUES(127,59,1,'a te gne si je cherche avec toi ? Habituellement Hanako m''aide, mais vu qu''elle n''est pas l...',16,0,0);
INSERT INTO seqtext VALUES(127,60,1,'a me prend un moment avant de raliser ce qu''elle veut dire.',1,0,0);
INSERT INTO seqtext VALUES(127,61,1,'Considrant qu''elle n''est pas capable de lire les emballages, faire ses courses sans aide doit tre trs difficile pour elle.',1,0,0);
INSERT INTO seqtext VALUES(127,62,1,'Cela dit, je ne peux pas me retirer de la tte qu''elle a eu l''ide de le faire quand j''ai dit que je venais ici.',1,0,0);
INSERT INTO seqtext VALUES(127,63,1,'Bien sr. a serait avec plaisir.',12,0,0);
INSERT INTO seqtext VALUES(127,64,1,'Je prends deux paniers rouges un peu uss dans la petite pile  l''entre, en donnant un  Lilly.',1,0,0);
INSERT INTO seqtext VALUES(127,65,3,'lilly',497,400,10);
INSERT INTO seqtext VALUES(127,65,3,'lilly',163,400,10);
INSERT INTO seqtext VALUES(127,65,3,'lilly',163,400,10);
INSERT INTO seqtext VALUES(127,65,1,'Elle le pose sur le sol, met son cartable dedans, rtracte sa canne et la glisse  travers les poignes du panier avant de le ramasser avec sa main droite.',1,0,0);
INSERT INTO seqtext VALUES(127,66,1,'Attends, si elle n''utilise pas sa canne...',1,0,0);
INSERT INTO seqtext VALUES(127,67,3,'lilly',163,400,10);
INSERT INTO seqtext VALUES(127,67,3,'lilly',147,400,10);
INSERT INTO seqtext VALUES(127,67,1,'Avant que je puisse finir ma pense, elle vient  ct de moi et attrape la manchette de mon uniforme de ses doigts fins.',1,0,0);
INSERT INTO seqtext VALUES(127,68,3,'lilly',446,240,10);
INSERT INTO seqtext VALUES(127,68,1,'a ne te gne pas ?',16,0,0);
INSERT INTO seqtext VALUES(127,69,1,'Ah, pas de problme.',12,0,0);
INSERT INTO seqtext VALUES(127,70,3,'lilly',147,240,10);
INSERT INTO seqtext VALUES(127,70,1,'Je n''ai aucune raison de refuser. Il y a des choses pires que de faire les courses avec une jolie fille accroche  moi, mme si c''est par ncessit.',1,0,0);
INSERT INTO seqtext VALUES(127,71,1,'Nous poursuivons notre route dans le magasin, et aucun des occasionnels clients qui passent n''ont l''air de ciller.',1,0,0);
INSERT INTO seqtext VALUES(127,72,1,'Considrant  quel point Yamaku est prs d''ici, j''imagine que voir des tudiants de l-bas doit tre tout  fait normal pour les rsidents locaux.',1,0,0);
INSERT INTO seqtext VALUES(127,73,1,'Pendant que nous parcourons chaque alle, je vrifie rapidement avec Lilly et trouve ce qu''elle cherche. Je prends en mme temps ce dont j''ai besoin, et mets nos articles dans nos paniers respectifs.',1,0,0);
INSERT INTO seqtext VALUES(127,74,1,'J''imagine que c''est la mme routine que Hanako et elle suivent chaque vendredi.',1,0,0);
INSERT INTO seqtext VALUES(127,75,1,'Bien, il reste que le pain et a devrait tre bon pour moi. Tu as besoin de quelque chose d''autre, Lilly ?',12,0,0);
INSERT INTO seqtext VALUES(127,76,3,'lilly',275,240,10);
INSERT INTO seqtext VALUES(127,76,1,'Non, a devrait tre tout.',16,0,0);
INSERT INTO seqtext VALUES(127,77,1,'Allons-y alors.',12,0,0);
INSERT INTO seqtext VALUES(127,78,3,'lilly',147,240,10);
INSERT INTO seqtext VALUES(127,78,1,'Avec un crochet rapide  la section boulangerie, nous nous dirigeons vers le comptoir.',1,0,0);
INSERT INTO seqtext VALUES(127,79,1,'La file d''attente, heureusement, est presque inexistante. Il ne faut pas longtemps avant que nous payions tous les deux pour notre nourriture et passions la porte.',1,0,0);
INSERT INTO seqtext VALUES(127,80,2,'',409,0,0);
INSERT INTO seqtext VALUES(127,81,1,'Pendant que Lilly tend sa canne sur toute sa longueur, je perds une minute  regarder le ciel nocturne, tenant nos deux sacs.',1,0,0);
INSERT INTO seqtext VALUES(127,82,1,'Pendant un moment j''essaye de faire de la fume avec mon souffle, mais la chaleur de l''t ne semble pas cooprer.',1,0,0);
INSERT INTO seqtext VALUES(127,83,1,'Finalement elle se redresse, s''tire rapidement avant de prendre son sac et de me laisser avec les deux miens.',1,0,0);
INSERT INTO seqtext VALUES(127,84,2,'',322,0,0);
INSERT INTO seqtext VALUES(127,85,3,'lilly',83,400,10);
INSERT INTO seqtext VALUES(127,85,1,'T''es fatigue aussi ?',12,0,0);
INSERT INTO seqtext VALUES(127,86,3,'lilly',610,400,10);
INSERT INTO seqtext VALUES(127,86,1,'Les prparations du festival ont t un chaos total. Shizune me marchant sur les talons n''arrange pas les choses, non plus.',16,0,0);
INSERT INTO seqtext VALUES(127,87,1,'H, elle essayait juste de faire en sorte que tout soit organis. Vaut mieux maintenant que plus tard, non ?',12,0,0);
INSERT INTO seqtext VALUES(127,88,3,'lilly',507,400,10);
INSERT INTO seqtext VALUES(127,88,1,'Je suppose. Je vais apprcier de me dtendre en ville demain, a c''est certain.',16,0,0);
INSERT INTO seqtext VALUES(127,89,1,'Je suppose que les prparations du festival doivent se faire sentir sur elles deux.',1,0,0);
INSERT INTO seqtext VALUES(127,90,2,'',400,0,0);
INSERT INTO seqtext VALUES(127,91,1,'Nous marchons dans la rue calme, parlant entre nous tout en portant nos sacs de nourriture et de fournitures du magasin.',1,0,0);
INSERT INTO seqtext VALUES(127,92,1,'...Attends, c''est quoi a ?',1,0,0);
INSERT INTO seqtext VALUES(127,93,1,'Je remarque une personne trs distinctive faisant chemin vers nous, sa silhouette claire par les rverbres.',1,0,0);
INSERT INTO seqtext VALUES(127,94,1,'Pendant une seconde je crois que c''est un autre tudiant de ma classe, mais quand il, ou devrais-je dire elle, se rapproche, je la reconnais rapidement.',1,0,0);
INSERT INTO seqtext VALUES(127,95,3,'bg',400,400,10);
INSERT INTO seqtext VALUES(127,95,3,'rin',23,650,10);
INSERT INTO seqtext VALUES(127,95,1,'Rin ? Qu''est-ce que tu fais ici aussi tard ?',12,0,0);
INSERT INTO seqtext VALUES(127,96,3,'lilly',30,400,10);
INSERT INTO seqtext VALUES(127,96,1,'Rin ?',16,0,0);
INSERT INTO seqtext VALUES(127,97,1,'Lilly penche la tte, ayant l''air d''essayer de se concentrer pour mieux couter. Il me vient soudainement que je devrais probablement lui reprsenter la scne.',1,0,0);
INSERT INTO seqtext VALUES(127,98,1,'C''est Rin... Tezuka, je crois que c''est son nom de famille, elle est de notre cole.',12,0,0);
INSERT INTO seqtext VALUES(127,99,3,'lilly',507,400,10);
INSERT INTO seqtext VALUES(127,99,1,'Elle se raidit au nom et affiche une expression complique, quelque chose comme une fusion force entre un sourire tranquille et une grimace douloureuse.',1,0,0);
INSERT INTO seqtext VALUES(127,100,1,'Ah. Je comprends.',16,0,0);
INSERT INTO seqtext VALUES(127,101,1,'J''imagine que Lilly connat Rin aussi.',1,0,0);
INSERT INTO seqtext VALUES(127,102,3,'rin',134,650,10);
INSERT INTO seqtext VALUES(127,102,3,'bg',400,320,10);
INSERT INTO seqtext VALUES(127,102,3,'rin',134,560,10);
INSERT INTO seqtext VALUES(127,102,3,'lilly',507,240,10);
INSERT INTO seqtext VALUES(127,102,1,'Rin se tourne vers nous, ayant l''air terriblement absente. J''hsite un peu entre le fait qu''elle ne nous reconnaisse pas et celui qu''elle ne le montre pas.',1,0,0);
INSERT INTO seqtext VALUES(127,103,1,'Elle a l''air d''un zombie. Ou d''une statue. Une statue d''un zombie.',1,0,0);
INSERT INTO seqtext VALUES(127,104,1,'Mais doucement, certains signes de comprhension semblent apparatre dans ses yeux sombres : c''est quelque chose qui a d la faire ragir.',1,0,0);
INSERT INTO seqtext VALUES(127,105,3,'rin',544,560,10);
INSERT INTO seqtext VALUES(127,105,3,'rin',134,560,10);
INSERT INTO seqtext VALUES(127,105,1,'Rin cligne une fois des yeux. Trs doucement.',1,0,0);
INSERT INTO seqtext VALUES(127,106,3,'rin',177,560,10);
INSERT INTO seqtext VALUES(127,106,1,'Bonjour.',15,0,0);
INSERT INTO seqtext VALUES(127,107,1,'',1,0,0);
INSERT INTO seqtext VALUES(127,108,1,'C''est une pause un peu gnante, tout le monde attendant que quelqu''un d''autre dise quelque chose.',1,0,0);
INSERT INTO seqtext VALUES(127,109,1,'Qu''est-ce que tu fais ici aussi tard ?',12,0,0);
INSERT INTO seqtext VALUES(127,110,1,'',1,0,0);
INSERT INTO seqtext VALUES(127,111,1,'Je...',15,0,0);
INSERT INTO seqtext VALUES(127,112,3,'rin',512,560,10);
INSERT INTO seqtext VALUES(127,112,1,'Je me le demandais aussi.  l''instant.',15,0,0);
INSERT INTO seqtext VALUES(127,113,3,'rin',348,560,10);
INSERT INTO seqtext VALUES(127,113,1,'Des gens m''ont pos la question juste avant. Je suppose qu''ils se demandaient la mme chose.',15,0,0);
INSERT INTO seqtext VALUES(127,114,1,'Je ne savais pas. Ils ne savaient pas non plus. J''ai pos la question. C''est pourquoi je me demande.',15,0,0);
INSERT INTO seqtext VALUES(127,115,1,'Donc c''tait plutt a. C''est un meurtre mystrieux sans meurtre.',15,0,0);
INSERT INTO seqtext VALUES(127,116,1,'',1,0,0);
INSERT INTO seqtext VALUES(127,117,3,'rin',124,560,10);
INSERT INTO seqtext VALUES(127,117,1,'Ils allaient par l.',15,0,0);
INSERT INTO seqtext VALUES(127,118,3,'rin',177,560,10);
INSERT INTO seqtext VALUES(127,118,1,'Elle se tourne vers la droite pour montrer la direction qu''ont pris les autres gens comme si c''tait important, et repivote le dos comme un pantin mcanique dans l''un de leurs mcanismes trs compliqus.',1,0,0);
INSERT INTO seqtext VALUES(127,119,1,'Pour une personne qui donne l''impression d''tre du type tranquille, Rin utilise vraiment beaucoup de mots pour dire des choses qui n''ont pas besoin d''tre dites.',1,0,0);
INSERT INTO seqtext VALUES(127,120,1,'Pas sr qu''elle ait termin, je ne dis rien. Pareil pour Lilly, qui semble tout aussi pauvre en mots pour le moment.',1,0,0);
INSERT INTO seqtext VALUES(127,121,1,'Je pense qu''en fait nous avons tous les deux peur qu''une rponse puisse provoquer une suite de sa part.',1,0,0);
INSERT INTO seqtext VALUES(127,122,1,'Notre manque vident de raction ne dconcerte pas du tout Rin. Elle continue de nous regarder dans l''expectative, un soupon d''expression calme sur son visage vierge.',1,0,0);
INSERT INTO seqtext VALUES(127,123,1,'Elle semble tre ce type de personne. Toujours aussi dtendue.',1,0,0);
INSERT INTO seqtext VALUES(127,124,1,'Comme si du sdatif pour lphant coulait dans ses veines  la place du sang.',1,0,0);
INSERT INTO seqtext VALUES(127,125,3,'lilly',509,240,10);
INSERT INTO seqtext VALUES(127,125,1,'Tu souffres d''amnsie ? Je ne me souviens pas que tu aies quelque chose de la sorte, cependant...',16,0,0);
INSERT INTO seqtext VALUES(127,126,1,'Non, je ne pense pas que ce soit a.',12,0,0);
INSERT INTO seqtext VALUES(127,127,1,'Les autres passants devaient juste tre inquiets, je pense. Tu as l''air vraiment perdu,  rester debout l au milieu de la rue.',12,0,0);
INSERT INTO seqtext VALUES(127,128,3,'rin',512,560,10);
INSERT INTO seqtext VALUES(127,128,1,'Oh, je vois.',15,0,0);
INSERT INTO seqtext VALUES(127,129,3,'rin',23,560,10);
INSERT INTO seqtext VALUES(127,129,1,'Peut-tre que je devrais prendre un autre genre de pose dans ce cas.',15,0,0);
INSERT INTO seqtext VALUES(127,130,1,'Je me demande pendant un moment si je dois poursuivre dans ce sens, ou abandonner dans l''intrt de ma propre sant mentale.',1,0,0);
INSERT INTO seqtext VALUES(127,131,1,'Je choisis la deuxime solution.',1,0,0);
INSERT INTO seqtext VALUES(127,132,1,'Apparemment la plupart du temps, il vaut mieux ne pas approfondir ce dont parle Rin.',1,0,0);
INSERT INTO seqtext VALUES(127,133,1,'Parler avec Rin est comme jouer aux checs avec un supercalculateur qui fait des mouvements totalement alatoires comme pour se moquer de tout ce que vous savez  propos des checs. C''est comme a, mais avec l''interaction humaine.',1,0,0);
INSERT INTO seqtext VALUES(127,134,1,'Et mme si je gagne, j''ai l''impression d''avoir perdu.',1,0,0);
INSERT INTO seqtext VALUES(127,135,1,'Maldiction, exactement comme Kenji l''a dit. Mme quand je gagne, je perds. Est-ce l le pouvoir des filles de Yamaku ?',1,0,0);
INSERT INTO seqtext VALUES(127,136,1,'',1,0,0);
INSERT INTO seqtext VALUES(127,137,1,'Je pousse cette pense de ct, elle est trop dangereuse pour continuer plus loin. C''est probablement juste la propagande anti-fministe de Kenji qui m''atteint dans un moment de faiblesse.',1,0,0);
INSERT INTO seqtext VALUES(127,138,1,'Ouais, peut-tre que prendre une autre pose aurait march.',12,0,0);
INSERT INTO seqtext VALUES(127,139,1,'Et de toute faon, tu as une ide de ce que tu fais l ?',12,0,0);
INSERT INTO seqtext VALUES(127,140,3,'rin',586,560,10);
INSERT INTO seqtext VALUES(127,140,1,'Elle fronce les sourcils, l''air extrmement mcontent  cause de ma question, ses consquences, ou de la rponse qu''elle va donner.',1,0,0);
INSERT INTO seqtext VALUES(127,141,1,'J''en ai une. Une ide. Je ne peux pas vraiment dire quel genre d''ide.',15,0,0);
INSERT INTO seqtext VALUES(127,142,3,'lilly',602,240,10);
INSERT INTO seqtext VALUES(127,142,1,'a a l''air de progresser, au moins.',16,0,0);
INSERT INTO seqtext VALUES(127,143,1,'Lilly a l''air d''avoir repr une ouverture pour une sorte de conversation normale. Je ne peux pas dire que je partage son optimiste.',1,0,0);
INSERT INTO seqtext VALUES(127,144,1,'Oui, il y a du progrs. Dfinitivement. Le reste viendra plus tard.',15,0,0);
INSERT INTO seqtext VALUES(127,145,3,'rin',504,560,10);
INSERT INTO seqtext VALUES(127,145,3,'lilly',507,240,10);
INSERT INTO seqtext VALUES(127,145,1,'J''en suis sre. J''ai toujours des... raisons.',15,0,0);
INSERT INTO seqtext VALUES(127,146,1,'Le silence qui suit ne tue l''esprance de Lilly que trop visiblement. a n''a pas dur longtemps.',1,0,0);
INSERT INTO seqtext VALUES(127,147,1,'Pour Rin, pour autant que je puisse dire, sans fondement, conviction mise  part... qu''est-ce qui devrait tre fait ?',1,0,0);
INSERT INTO seqtext VALUES(127,148,1,'On pourrait juste la laisser  ses trucs, quels qu''ils soient... mais il est tard et je ne pense pas que nous serions remercis si Rin est trouve se tenant ici au milieu de la nuit.',1,0,0);
INSERT INTO seqtext VALUES(127,149,1,'Et elle y sera srement, sauf si elle parvient  se rappeler ce qu''elle faisait ici  l''origine.',1,0,0);
INSERT INTO seqtext VALUES(127,150,1,'Quant  moi, s''il faut essayer de deviner ce qui lui est pass par la tte quand elle a dcid de s''embarquer dans cette aventure, les chances semblent gales  celles de gagner  la loterie.',1,0,0);
INSERT INTO seqtext VALUES(127,151,1,'Plusieurs fois d''affile.',1,0,0);
INSERT INTO seqtext VALUES(127,152,1,'Lilly est trangement silencieuse aussi. J''apprcierais un certain appui de sa part  ce moment, spcialement si elle est plus habitue  Rin que je le suis.',1,0,0);
INSERT INTO seqtext VALUES(127,153,1,'Mais on n''y peut rien. Il semble que sa familiarit avec Rin soit exactement la raison pour laquelle elle est reste discrte.',1,0,0);
INSERT INTO seqtext VALUES(127,154,1,'Donc, je suppose que tu allais quelque part, tu ne revenais pas  l''cole... une ide de o ?',12,0,0);
INSERT INTO seqtext VALUES(127,155,3,'rin',383,560,10);
INSERT INTO seqtext VALUES(127,155,3,'lilly',30,240,10);
INSERT INTO seqtext VALUES(127,155,1,'Ses yeux s''agrandissent sous le choc et elle recule d''une faon un peu artificielle, rendant vident le fait qu''elle ait dj agi de cette faon lors de situations de ce genre.',1,0,0);
INSERT INTO seqtext VALUES(127,156,1,'Tu lis dans les penses ? C''est a ton handicap ? Vraiment unique !',15,0,0);
INSERT INTO seqtext VALUES(127,157,1,'Non... Quoi ? Pourquoi tu penserais a ?',12,0,0);
INSERT INTO seqtext VALUES(127,158,3,'rin',211,560,10);
INSERT INTO seqtext VALUES(127,158,3,'lilly',83,240,10);
INSERT INTO seqtext VALUES(127,158,1,'Tu savais ce que je faisais.',15,0,0);
INSERT INTO seqtext VALUES(127,159,3,'lilly',77,240,10);
