INSERT INTO seqtext VALUES(133,2,2,'',451,0,0);
INSERT INTO seqtext VALUES(133,3,1,'Les tudiants rentrent dans la classe pour les cours de samedi, chacun et chacune d''entre eux ont les yeux fatigus d''avoir travaill toute la nuit.',1,0,0);
