INSERT INTO seqtext VALUES(136,48,1,' y penser, ce n''est pas celui que je l''ai vue lire  la bibliothque. Elle doit lire plutt rapidement pour les enchaner  ce rythme.',1,0,0);
INSERT INTO seqtext VALUES(137,1,1,'Aprs dix minutes sur son sige  tourner les pages et essayer de lire, Hanako ferme son livre et part aussi.',1,0,0);
