INSERT INTO seqtext VALUES(138,2,4,'hanako',0,0,0);
INSERT INTO seqtext VALUES(138,3,1,'Aprs dix minutes sur son sige  tourner les pages et essayer de lire, Hanako ferme son livre et part aussi.',1,0,0);
