INSERT INTO seqtext VALUES(138,7,3,'',336,400,10);
INSERT INTO seqtext VALUES(138,7,1,'L''cole est une ruche d''activits, mais personne ne prte attention  moi.',1,0,0);
INSERT INTO seqtext VALUES(138,8,1,'Je flne prs des classes remplies d''tudiants faisant frntiquement ceci et cela, bourdonnant partout comme des petites abeilles travailleuses.',1,0,0);
