INSERT INTO seqtext VALUES(138,10,2,'',186,0,0);
INSERT INTO seqtext VALUES(138,11,3,'',336,400,10);
INSERT INTO seqtext VALUES(138,11,1,'C''est un peu plus silencieux dehors, mais pas tellement.',1,0,0);
INSERT INTO seqtext VALUES(138,12,1,'Les gens passent par l,  gauche et  droite, se dpchant aussi vite qu''ils le peuvent; occups et nergiques.',1,0,0);
INSERT INTO seqtext VALUES(138,13,1,'Je me sens  l''oppos. Le soleil de midi semble drainer toute l''nergie de mon corps, le rendant tout mou.',1,0,0);
INSERT INTO seqtext VALUES(138,14,1,'De doux et lgers courants d''air passent dans ma chemise, donnant l''impression d''tre des coussins.',1,0,0);
INSERT INTO seqtext VALUES(138,15,1,'Je bille paresseusement, pensant  ce que je pourrais faire.',1,0,0);
INSERT INTO seqtext VALUES(138,16,1,'Je vais dposer mes livres au dortoir dj, et puis... quelque chose que je n''ai pas encore dcid.',1,0,0);
INSERT INTO seqtext VALUES(138,17,1,'Peut-tre que Kenji est dans sa chambre.',1,0,0);
INSERT INTO seqtext VALUES(138,18,2,'',454,0,0);
INSERT INTO seqtext VALUES(138,19,1,'Sur le chemin des dortoirs, j''aperois Emi courant dans ma direction en dpit du fait qu''elle n''a pas ses tranges prothses de course. Je lui fais un signe et elle s''arrte dans un drapage.',1,0,0);
INSERT INTO seqtext VALUES(138,20,3,'emi',101,400,10);
INSERT INTO seqtext VALUES(138,20,1,'Yo, Hisao !',14,0,0);
INSERT INTO seqtext VALUES(138,21,1,'Des claboussures de peinture blanche et verte ornent son nez et son menton, mais elle affiche un large sourire, comme elle semble toujours le faire.',1,0,0);
INSERT INTO seqtext VALUES(138,22,3,'emi',393,400,10);
INSERT INTO seqtext VALUES(138,22,1,'Elle se rapproche encore plus de moi, amplifiant le sentiment qu''elle m''examine.',1,0,0);
INSERT INTO seqtext VALUES(138,23,1,'Qu''est-ce''tu fais ?',14,0,0);
INSERT INTO seqtext VALUES(138,24,1,'Rien, vraiment. Je n''ai rien  faire pour le festival et tout le monde semble avoir quelque chose d''important  faire.',12,0,0);
INSERT INTO seqtext VALUES(138,25,3,'emi',495,400,10);
INSERT INTO seqtext VALUES(138,25,1,'C''est parfait ! Alors tu peux nous aider Rin et moi !',14,0,0);
INSERT INTO seqtext VALUES(138,26,1,'Avec les prparations du festival ? Euuh, je ne suis pas sr de pouvoir beaucoup vous aider.',12,0,0);
INSERT INTO seqtext VALUES(138,27,3,'emi',321,400,10);
INSERT INTO seqtext VALUES(138,27,1,'C''est suffisant ! Je n''aide pas beaucoup non plus !',14,0,0);
INSERT INTO seqtext VALUES(138,28,1,'Emi attrape mon poignet et commence  me tirer  l''intrieur de l''cole avec une grande force.',1,0,0);
INSERT INTO seqtext VALUES(138,29,2,'',609,0,0);
INSERT INTO seqtext VALUES(138,30,3,'',336,400,10);
INSERT INTO seqtext VALUES(138,30,1,'Mme sa vitesse de marche ressemble  du jogging, manquant de me faire tomber plusieurs fois juste en essayant de la suivre.',1,0,0);
INSERT INTO seqtext VALUES(138,31,2,'',157,0,0);
INSERT INTO seqtext VALUES(138,32,1,'Les escaliers ralentissent un peu Emi. Peut-tre qu''il est difficile de monter avec ses jambes, ou peut-tre qu''elle est finalement  bout de souffle.',1,0,0);
INSERT INTO seqtext VALUES(138,33,2,'',55,0,0);
INSERT INTO seqtext VALUES(138,34,3,'',336,400,10);
INSERT INTO seqtext VALUES(138,34,1,'Nous parcourons tout le chemin jusqu''au troisime tage et au couloir des ans, finissant l o j''tais il y a cinq minutes. J''aurais pu tout aussi bien rester ici et attendre Emi, si j''avais su.',1,0,0);
INSERT INTO seqtext VALUES(138,35,1,'Donc tu es... et Rin travaille toujours son mur ?',12,0,0);
INSERT INTO seqtext VALUES(138,36,3,'emi',101,400,10);
INSERT INTO seqtext VALUES(138,36,1,'C''est exact ! Elle a besoin de plein de types de peintures et de pinceaux et de trucs, donc je viens les chercher dans la classe d''art.',14,0,0);
INSERT INTO seqtext VALUES(138,37,1,'Et tu as besoin de moi pour aider.',12,0,0);
INSERT INTO seqtext VALUES(138,38,3,'emi',475,400,10);
INSERT INTO seqtext VALUES(138,38,1,'Eh bien... Rin m''a dit que tu l''avais dj aide, donc j''ai pens que a ne te drangerait pas.',14,0,0);
INSERT INTO seqtext VALUES(138,39,1,'Je vois.',12,0,0);
INSERT INTO seqtext VALUES(138,40,2,'',377,0,0);
INSERT INTO seqtext VALUES(138,41,1,'Donc merci  l''trange logique d''Emi, je suis encore une fois ici, rapportant des trucs depuis la classe d''art  d''autres personnes.',1,0,0);
INSERT INTO seqtext VALUES(138,42,1,'La pice est vide  part nous-mmes et les solitaires paillettes de poussire flottant dans les airs. Emi avance directement vers le mur du fond, sortant un petit morceau de papier froiss de sa poche.',1,0,0);
INSERT INTO seqtext VALUES(138,43,1,'Pendant qu''elle essaye de donner un sens  l''criture griffonne, je jette un il au matriel qui trane ici.',1,0,0);
INSERT INTO seqtext VALUES(138,44,1,'Des dizaines de pots de peinture et de bouteilles sont disposs sur des planches d''une manire bien peu organise.',1,0,0);
INSERT INTO seqtext VALUES(138,45,1,'Certains donnent l''impression d''tre l depuis plusieurs dcennies; des reliques des prcdentes gnrations du club d''art.',1,0,0);
INSERT INTO seqtext VALUES(138,46,1,' ct des gros tas de papier  dessin soigneusement empils, il y a des botes pleines de pinceaux et de crayons de tailles et couleurs diffrentes.',1,0,0);
INSERT INTO seqtext VALUES(138,47,1,'L''odeur de peinture, de dissolvant et de papier frais flotte dans l''air vici, mlange dans mes narines pour former ce parfum unique de l''art.',1,0,0);
INSERT INTO seqtext VALUES(138,48,3,'emi',101,1200,10);
INSERT INTO seqtext VALUES(138,48,2,'',377,0,0);
INSERT INTO seqtext VALUES(138,49,3,'emi',101,650,10);
INSERT INTO seqtext VALUES(138,49,1,'Emi regarde attentivement ses notes, les comparant aux marques sur les pots de peinture, et me les passe quand elle trouve des correspondances.',1,0,0);
INSERT INTO seqtext VALUES(138,50,3,'emi',475,650,10);
INSERT INTO seqtext VALUES(138,50,1,'Elle est debout sur la pointe des pieds pour regarder sur la planche du haut, mais ce n''est pas suffisant.',1,0,0);
INSERT INTO seqtext VALUES(138,51,3,'emi',209,650,10);
INSERT INTO seqtext VALUES(138,51,1,'Le niveau de ses yeux est en dessous de la planche quoi qu''elle fasse. Emi abandonne et regarde juste l''tagre avec envie, comme un enfant dans un magasin de jouet, soufflant dans l''agacement.',1,0,0);
INSERT INTO seqtext VALUES(138,52,3,'emi',549,400,10);
INSERT INTO seqtext VALUES(138,52,1,'Aprs un moment de colre, elle commence  sauter, cherchant apparemment  lire trs rapidement les tiquettes au cours de la fraction de seconde o elle peut les voir, et attraper ce qu''elle peut.',1,0,0);
INSERT INTO seqtext VALUES(138,53,3,'emi',484,650,10);
INSERT INTO seqtext VALUES(138,53,1,'C''est sans surprise qu''elle choue lamentablement, et russit presque  faire s''effondrer l''tagre entire.',1,0,0);
INSERT INTO seqtext VALUES(138,54,1,'Maintenant je vois pourquoi il serait utile que je donne un coup de main ici.',1,0,0);
INSERT INTO seqtext VALUES(138,55,1,'Allez, laisse-moi faire. Tu ne peux pas sauter assez haut, et je ne veux pas que tu te fasses mal en essayant.',12,0,0);
INSERT INTO seqtext VALUES(138,56,1,'Et aussi, je dois faire deux fois ta taille.',12,0,0);
INSERT INTO seqtext VALUES(138,57,3,'emi',35,650,10);
INSERT INTO seqtext VALUES(138,57,1,'C''est pas vrai !',14,0,0);
INSERT INTO seqtext VALUES(138,58,1,'Elle se retourne, regard mprisant, joues rouges et tout.',1,0,0);
INSERT INTO seqtext VALUES(138,59,1,'Je rigole, je rigole. Quoi qu''il en soit, je vais voir l-haut, ok ?',12,0,0);
INSERT INTO seqtext VALUES(138,60,3,'emi',209,650,10);
INSERT INTO seqtext VALUES(138,60,1,'Elle me fusille encore une fois du regard, mais n''arrive pas  trouver de riposte. Avec un hmph, elle se retourne vers moi.',1,0,0);
INSERT INTO seqtext VALUES(138,61,4,'emi',0,0,0);
INSERT INTO seqtext VALUES(138,62,1,'Donc je commence  regarder sur le haut pour la peinture pendant que plus bas, Emi se tapit pour rcuprer ce qu''elle peut des placards.',1,0,0);
INSERT INTO seqtext VALUES(138,63,1,'Je secoue un peu la tte, aprs avoir vrifi deux fois pour tre sr qu''elle ne peut pas me voir le faire.',1,0,0);
INSERT INTO seqtext VALUES(138,64,1,'Que Emi ait un complexe  propos de sa taille est une surprise; je n''aurais pas plaisant  ce sujet autrement.',1,0,0);
INSERT INTO seqtext VALUES(138,65,1,'Elle a l''air facile  vivre, mais j''imagine que tout le monde a ses points faibles.',1,0,0);
INSERT INTO seqtext VALUES(138,66,3,'emi',475,400,10);
INSERT INTO seqtext VALUES(138,66,1,'Seulement aprs que nous ayons collect et align presque tous les objets sur un bureau comme le butin d''un chasseur de trsors, je ralise que ce n''tait pas ncessairement la pique sur sa taille qui l''avait nerve.',1,0,0);
INSERT INTO seqtext VALUES(138,67,1,'Elle ne doit pas aimer qu''on lui dise qu''elle ne peut pas faire quelque chose. Comme sauter.',1,0,0);
INSERT INTO seqtext VALUES(138,68,1,'Mais Emi elle-mme semble avoir dj tout oubli. Prompte  la colre, prompte  pardonner... elle est ce type de personne ?',1,0,0);
INSERT INTO seqtext VALUES(138,69,1,'Au moins, elle ne semble pas avoir pris quoi que ce soit  cur, vu que nous bavardons joyeusement pendant que nous ramassons le reste de nos objets et faisons le chemin du retour vers Rin.',1,0,0);
INSERT INTO seqtext VALUES(138,70,2,'',186,0,0);
INSERT INTO seqtext VALUES(138,71,3,'',336,400,10);
INSERT INTO seqtext VALUES(138,71,1,'Je porte chevaleresquement la majeure partie du matriel que nous acheminons vers les dortoirs.',1,0,0);
INSERT INTO seqtext VALUES(138,72,3,'emi',209,400,10);
INSERT INTO seqtext VALUES(138,72,1,'Rin est vraiment stresse pour finir sa peinture. C''est sa faute cependant; elle aurait d commencer plus tt.',1,0,0);
INSERT INTO seqtext VALUES(138,73,1,'Elle va russir  finir ?',12,0,0);
INSERT INTO seqtext VALUES(138,74,3,'emi',101,400,10);
INSERT INTO seqtext VALUES(138,74,1,'Aucune ide. Je dirais que c''est bon, mais avec Rin, tu ne sais jamais ce qui se passe.',14,0,0);
INSERT INTO seqtext VALUES(138,75,3,'emi',209,400,10);
INSERT INTO seqtext VALUES(138,75,1,'Je l''ai trouve ce matin allonge devant les dortoirs en position ftale. Elle n''avait pas dormi de la nuit. Je n''arrive pas  croire que les infirmiers de nuit ne l''aient pas vue.',14,0,0);
INSERT INTO seqtext VALUES(138,76,3,'emi',475,400,10);
INSERT INTO seqtext VALUES(138,76,1,'Et maintenant elle peint encore comme une folle.',14,0,0);
INSERT INTO seqtext VALUES(138,77,1,'Ouais, j''ai... remarqu qu''elle tait un peu comme... dtraque. Pour ainsi dire.',12,0,0);
INSERT INTO seqtext VALUES(138,78,3,'emi',597,400,10);
INSERT INTO seqtext VALUES(138,78,1,'Ce qui fait rire Emi, ainsi que ma vraisemblablement trop vidente maladresse.',1,0,0);
INSERT INTO seqtext VALUES(138,79,3,'emi',20,400,10);
INSERT INTO seqtext VALUES(138,79,1,'a ne me gne pas. Elle est juste un peu trange parfois.',14,0,0);
INSERT INTO seqtext VALUES(138,80,1,'L-dessus, je suis d''accord avec elle. Contrairement  moi, Emi semble tre calme avec Rin... quoi qu''elle puisse ressentir  son sujet.',1,0,0);
INSERT INTO seqtext VALUES(138,81,1,'Pourtant, elles ne semblent pas proches comme Misha et Shizune le sont. Avec elles agissant comme une seule entit, parfois, il est dur de dire o l''une finit et o l''autre commence.',1,0,0);
INSERT INTO seqtext VALUES(138,82,1,'Mme si elles sont si diffrentes, tout comme Emi et Rin le sont.',1,0,0);
INSERT INTO seqtext VALUES(138,83,1,'Et Rin est la plus diffrente de toutes, diffrente de tous les autres que j''ai rencontrs.',1,0,0);
INSERT INTO seqtext VALUES(138,84,1,'Ouais, je crois que c''est une personne... vraiment unique.',12,0,0);
INSERT INTO seqtext VALUES(138,85,1,'J''en reviens  ce mot encore une fois, comme s''il englobait la personnalit de Rin  lui tout seul, mais vraiment c''est juste un substitut  une longue description de ses singularits.',1,0,0);
INSERT INTO seqtext VALUES(138,86,3,'emi',597,400,10);
INSERT INTO seqtext VALUES(138,86,1,'Emi rit pendant que je cherche un mot qui la dcrirait correctement.',1,0,0);
INSERT INTO seqtext VALUES(138,87,3,'emi',475,400,10);
INSERT INTO seqtext VALUES(138,87,1,'Elle est juste trange.',14,0,0);
INSERT INTO seqtext VALUES(138,88,3,'emi',224,400,10);
INSERT INTO seqtext VALUES(138,88,1,'Tu sais, plus tt, elle a pass une demi-heure assise sur sa bote.',14,0,0);
INSERT INTO seqtext VALUES(138,89,1,' fixer ses orteils.',14,0,0);
INSERT INTO seqtext VALUES(138,90,3,'emi',597,400,10);
INSERT INTO seqtext VALUES(138,90,1,'Elle rit encore une fois d''une manire qui me fait penser qu''elle ne sait pas vraiment ce qui est drle  propos de a, juste que a l''est.',1,0,0);
INSERT INTO seqtext VALUES(138,91,3,'emi',475,400,10);
INSERT INTO seqtext VALUES(138,91,1,'Tout le temps.',14,0,0);
INSERT INTO seqtext VALUES(138,92,2,'',454,0,0);
INSERT INTO seqtext VALUES(138,93,1,'La zone de travail est sens dessus dessous, mais la peinture en elle-mme a pris encore plus de place sur le mur que la dernire fois.',1,0,0);
INSERT INTO seqtext VALUES(138,94,1,'Les visages humains dfigurs sont le plus souvent dans les tons rouge, rose, et orange; trange, imaginaire... trucs peuplant les espaces entre eux.',1,0,0);
INSERT INTO seqtext VALUES(138,95,1,'a a l''air... bien. Je ne peux pas penser  un mot qui pourrait dcrire le travail de manire concise et comprhensive alors je me contente d''un quelconque bien.',1,0,0);
INSERT INTO seqtext VALUES(138,96,1,'Mais honntement, on dirait que la zone autour du mur devient de plus en plus en dsordre au fur et  mesure que la peinture progresse.',1,0,0);
INSERT INTO seqtext VALUES(138,97,1,'Le sol est jonch de dizaines de pots de peinture, de fournitures d''art diverses et de bouteilles de soda vides.',1,0,0);
INSERT INTO seqtext VALUES(138,98,3,'rin',615,400,10);
INSERT INTO seqtext VALUES(138,98,1,'Rin elle-mme est au centre de ce chaos, debout avec un air trs dtendu comme si elle tait une part de la scne.',1,0,0);
INSERT INTO seqtext VALUES(138,99,1,'Le bas de son pantalon a t retrouss jusqu''aux genoux, laissant voir ses minces jambes qui portent le spectre de la guerre de peinture, similaire  celui sur le visage d''Emi.',1,0,0);
INSERT INTO seqtext VALUES(138,100,3,'emi',475,-400,10);
INSERT INTO seqtext VALUES(138,100,3,'rin',615,560,10);
INSERT INTO seqtext VALUES(138,100,3,'bg',454,480,10);
INSERT INTO seqtext VALUES(138,100,3,'emi',475,240,10);
INSERT INTO seqtext VALUES(138,100,1,'Emi court vers Rin en me passant devant, et saute joyeusement devant elle.',1,0,0);
INSERT INTO seqtext VALUES(138,101,3,'emi',597,240,10);
INSERT INTO seqtext VALUES(138,101,1,'Je suis de retour !',14,0,0);
INSERT INTO seqtext VALUES(138,102,3,'rin',518,560,10);
INSERT INTO seqtext VALUES(138,102,1,'C''tait rapide. T''as encore couru dans les couloirs ?',15,0,0);
INSERT INTO seqtext VALUES(138,103,3,'emi',224,240,10);
INSERT INTO seqtext VALUES(138,103,1,'Hisao m''a aide.',14,0,0);
INSERT INTO seqtext VALUES(138,104,3,'emi',475,240,10);
INSERT INTO seqtext VALUES(138,104,1,'Emi pointe victorieusement le doigt vers moi.',1,0,0);
INSERT INTO seqtext VALUES(138,105,3,'rin',588,560,10);
INSERT INTO seqtext VALUES(138,105,1,'Rin se tourne en suivant le doigt d''Emi avec ses yeux, et regarde dans ma direction.',1,0,0);
INSERT INTO seqtext VALUES(138,106,3,'rin',184,560,10);
INSERT INTO seqtext VALUES(138,106,1,'Elle hoche distraitement la tte vers moi. Elle a l''air de n''avoir pas dormi depuis la nuit dernire : un regard vide et glac se portant lgrement sur moi, et des mouvements comme dans un film au ralenti.',1,0,0);
INSERT INTO seqtext VALUES(138,107,1,'Bonjour, Hisao. Merci de ton aide.',15,0,0);
INSERT INTO seqtext VALUES(138,108,1,'N''en parlons plus.',12,0,0);
INSERT INTO seqtext VALUES(138,109,3,'rin',8,560,10);
INSERT INTO seqtext VALUES(138,109,1,'Je viens juste de le faire.',15,0,0);
INSERT INTO seqtext VALUES(138,110,1,'Laisse tomber.',12,0,0);
INSERT INTO seqtext VALUES(138,111,1,'On dirait que tu as progress. a a l''air bien, pour autant que je puisse dire.',12,0,0);
INSERT INTO seqtext VALUES(138,112,3,'rin',184,560,10);
INSERT INTO seqtext VALUES(138,112,1,'Mais maintenant tu as encore plus de malchance.',15,0,0);
INSERT INTO seqtext VALUES(138,113,1,'Je sais, mais je suis prt  prendre le risque.',12,0,0);
INSERT INTO seqtext VALUES(138,114,3,'rin',156,560,10);
INSERT INTO seqtext VALUES(138,114,1,'C''est une chose trs agrable  dire. Pour moi, videmment. Pas pour toi.',15,0,0);
INSERT INTO seqtext VALUES(138,115,3,'rin',518,560,10);
INSERT INTO seqtext VALUES(138,115,1,'C''est pourquoi les artistes sont toujours malchanceux. Ils doivent constamment regarder leurs peintures inacheves.',15,0,0);
INSERT INTO seqtext VALUES(138,116,1,'Donc les artistes ne peuvent pas trouver l''amour, leurs missions de tl sont annules, ou ils meurent jeunes  cause d''une maladie non prcise. C''est une profonde et mystrieuse loi de l''univers.',15,0,0);
INSERT INTO seqtext VALUES(138,117,3,'rin',96,560,10);
INSERT INTO seqtext VALUES(138,117,1,'Sauf s''ils sont aveugles.',15,0,0);
INSERT INTO seqtext VALUES(138,118,1,'Elle y rflchit pendant un moment, ayant l''air de pouvoir s''endormir  tout instant.',1,0,0);
INSERT INTO seqtext VALUES(138,119,3,'rin',588,560,10);
INSERT INTO seqtext VALUES(138,119,1,'Il y a un garon.',15,0,0);
INSERT INTO seqtext VALUES(138,120,3,'rin',184,560,10);
INSERT INTO seqtext VALUES(138,120,1,'Au club d''art, tu vois. Garon aveugle. Donc il ne peut pas. Voir.',15,0,0);
INSERT INTO seqtext VALUES(139,1,1,'Tu me l''as dj dit.',12,0,0);
INSERT INTO seqtext VALUES(139,2,3,'emi',296,240,10);
INSERT INTO seqtext VALUES(139,2,1,'Je regarde Emi du coin de l''il et elle me regarde en retour d''une faon qui me dit qu''elle a dj entendu cette histoire aussi.',1,0,0);
INSERT INTO seqtext VALUES(139,3,1,'Aucun de nous deux ne parle  Rin, donc elle continue son monologue monotone comme un comdien de stand-up pas drle.',1,0,0);
INSERT INTO seqtext VALUES(140,1,3,'rin',518,560,10);
INSERT INTO seqtext VALUES(140,1,1,'Il devrait devenir un artiste. Aucune malchance, c''est garanti.',15,0,0);
INSERT INTO seqtext VALUES(140,2,1,'Tu ne crois pas que a serait une bonne ide ?',15,0,0);
INSERT INTO seqtext VALUES(140,3,3,'rin',96,560,10);
INSERT INTO seqtext VALUES(140,3,1,'Que seules les personnes aveugles devraient devenir artistes ? Non, pas vraiment.',12,0,0);
INSERT INTO seqtext VALUES(140,4,1,'',1,0,0);
INSERT INTO seqtext VALUES(140,5,3,'rin',8,560,10);
INSERT INTO seqtext VALUES(140,5,1,'Tu marques un point.',15,0,0);
INSERT INTO seqtext VALUES(140,6,3,'rin',615,560,10);
INSERT INTO seqtext VALUES(140,6,1,'Abandonnant sa pense, elle se tourne  nouveau pour examiner son travail et commence  fredonner une chanson que je pense reconnatre, mais je ne me rappelle pas son nom.',1,0,0);
INSERT INTO seqtext VALUES(140,7,3,'emi',101,240,10);
INSERT INTO seqtext VALUES(140,7,1,'Emi dispose les fournitures que nous avons rapportes et dplace certains pots de peinture, essayant d''apporter une certaine organisation  la scne.',1,0,0);
INSERT INTO seqtext VALUES(140,8,3,'rin',518,560,10);
INSERT INTO seqtext VALUES(140,8,1,'Emi, j''ai besoin de la peinture bleu de Prusse.',15,0,0);
INSERT INTO seqtext VALUES(140,9,3,'emi',259,240,10);
INSERT INTO seqtext VALUES(140,9,1,'C''est laquelle bleu de Prusse...',14,0,0);
INSERT INTO seqtext VALUES(140,10,1,'Elle regarde, impuissante, sur sept ou huit botes, chacune avec un ton de bleu diffrent.',1,0,0);
INSERT INTO seqtext VALUES(140,11,3,'rin',8,560,10);
INSERT INTO seqtext VALUES(140,11,1,'C''est celle avec du bleu de Prusse  l''intrieur.',15,0,0);
INSERT INTO seqtext VALUES(140,12,3,'emi',209,240,10);
INSERT INTO seqtext VALUES(140,12,1,'Roh, Rin ! Tu n''aides pas du tout !',14,0,0);
INSERT INTO seqtext VALUES(140,13,1,'Je regarde autour aussi, mme en ne sachant pas non plus  quoi ressemble le bleu de Prusse. Je me demande ce que le bleu a  voir avec la Prusse.',1,0,0);
INSERT INTO seqtext VALUES(140,14,1,'...Ou ce qu''est la Prusse aussi. Le nom m''a l''air lgrement familier, mais je n''arrive pas  le replacer.',1,0,0);
INSERT INTO seqtext VALUES(140,15,1,'Mme si aucun des bleus n''a l''air d''tre plus Prussien qu''un autre, les petits caractres sur les tiquettes sont assez lisibles pour dterminer qu''aucun ne dit quoi que ce soit  propos du fait d''tre de Prusse.',1,0,0);
INSERT INTO seqtext VALUES(140,16,1,'Il n''y a pas de bleu de Prusse ici.',12,0,0);
INSERT INTO seqtext VALUES(141,1,2,'',454,0,0);
INSERT INTO seqtext VALUES(141,2,3,'rin',8,560,10);
INSERT INTO seqtext VALUES(141,2,3,'emi',209,240,10);
INSERT INTO seqtext VALUES(141,2,1,'Emi pousse un soupir.',1,0,0);
INSERT INTO seqtext VALUES(141,3,3,'emi',484,240,10);
INSERT INTO seqtext VALUES(141,3,1,'J''imagine que je dois retourner en chercher.',14,0,0);
INSERT INTO seqtext VALUES(141,4,3,'emi',475,240,10);
INSERT INTO seqtext VALUES(141,4,1,'J''ai promis d''aider sur notre projet de classe cependant, donc je vais revenir un peu plus tard. Tu peux t''en passer pendant quelques heures ?',14,0,0);
INSERT INTO seqtext VALUES(141,5,3,'rin',184,560,10);
INSERT INTO seqtext VALUES(141,5,1,'Rin hoche la tte, et donc Emi part.',1,0,0);
INSERT INTO seqtext VALUES(141,6,4,'emi',0,0,0);
INSERT INTO seqtext VALUES(141,7,3,'rin',184,400,10);
INSERT INTO seqtext VALUES(141,7,3,'bg',454,400,10);
INSERT INTO seqtext VALUES(141,7,1,'Je reste parce que j''aime regarder Rin peindre, et parce que je n''ai rien de mieux  faire.',1,0,0);
INSERT INTO seqtext VALUES(141,8,1,'Je m''assieds sur une bote et prends mon livre d''aujourd''hui dans mon sac. C''est une histoire  propos de trois gars buvant des bires pendant deux semaines sans faire grand-chose d''autre.',1,0,0);
INSERT INTO seqtext VALUES(141,9,4,'rin',0,0,0);
INSERT INTO seqtext VALUES(141,10,1,'Rin bouge de l''emplacement o elle avait besoin du bleu et commence  lentement travailler sur un autre.',1,0,0);
INSERT INTO seqtext VALUES(141,11,1,'Son pied travaille le pinceau rgulirement sur le mur en pltre. Couche de peinture sur couche de peinture. Le mur gagne lentement en forme.',1,0,0);
INSERT INTO seqtext VALUES(141,12,1,'Je tourne les pages  un rythme tranquille. Dans ce chapitre ils boivent de la bire chez le copain du protagoniste. Dans ceux prcdents c''tait dans l''appartement du protagoniste.',1,0,0);
INSERT INTO seqtext VALUES(141,13,1,'Ce n''est pas le genre de livre dont on a envie de tourner les pages, c''est la tranche de vie de quelqu''un d''imaginaire qui me fait me demander pourquoi elle a d tre crite.',1,0,0);
INSERT INTO seqtext VALUES(141,14,1,'Pourquoi, en effet. La raison pour faire quelque chose de cratif... c''est quelque chose d''incomprhensible.',1,0,0);
INSERT INTO seqtext VALUES(141,15,1,'Comme la raison pour laquelle Rin peint. On dirait que Emi et elle sont pareilles, aller  l''encontre de leur destin comme si c''tait juste par dpit.',1,0,0);
INSERT INTO seqtext VALUES(141,16,1,'Rin a mme dit quelque chose comme a. Faire quelque chose que tu ne peux pas faire juste parce que tu le peux.',1,0,0);
INSERT INTO seqtext VALUES(141,17,1,'C''est ce qu''elle voulait dire ? C''est a sa raison ? a peut tre celle d''Emi, elle se trouve tre une personne plutt ttue.',1,0,0);
INSERT INTO seqtext VALUES(141,18,1,'Rin ne donne pas cette impression-l.  bien y penser, elle ne donne aucune impression, ou alors une diffrente  chaque fois que je lui parle.',1,0,0);
INSERT INTO seqtext VALUES(141,19,1,'Pourquoi elle a dit ce qu''elle a dit ? Pourquoi est-ce qu''elle, ou mme quelqu''un d''autre, peint, ou dessine, ou sculpte, ou crit ?',1,0,0);
INSERT INTO seqtext VALUES(141,20,1,'Je n''ai jamais eu d''envies cratives donc je ne pense pas que je puisse vraiment comprendre.',1,0,0);
INSERT INTO seqtext VALUES(141,21,1,'a me donne l''impression d''tre vide  l''intrieur.',1,0,0);
INSERT INTO seqtext VALUES(141,22,1,'Quelle pense lugubre. Je ne peux pas vraiment dcider si c''est vrai ou pas, non plus.',1,0,0);
INSERT INTO seqtext VALUES(141,23,1,'Suis-je content d''tre comme a ? Je ne peux pas renier tre un peu envieux d''Emi, mais je ne peux pas vraiment considrer a comme un dfaut d''une quelconque manire.',1,0,0);
INSERT INTO seqtext VALUES(141,24,1,'Je suis moi et elle est elle.',1,0,0);
INSERT INTO seqtext VALUES(141,25,1,'Pourtant, j''ai besoin de trouver quelque chose. Quelque chose qui pourrait... faire que je me sente un peu moins perdu  mon sujet, au lieu de juste me noyer dans les livres comme je faisais  l''hpital.',1,0,0);
INSERT INTO seqtext VALUES(141,26,1,'Je ne peux pas m''empcher de me sentir dsorient; la nouvelle cole, le style de vie et les gens autour de moi contribuent lourdement  cette sensation.',1,0,0);
INSERT INTO seqtext VALUES(141,27,1,'Je fais de mon mieux pour m''intgrer dans les cercles sociaux existant, et la plupart des gens que j''ai rencontrs ont t trs sympas.',1,0,0);
INSERT INTO seqtext VALUES(141,28,1,'J''ai l''impression d''tre un tranger; comme si je n''tais pas l''un des leurs.',1,0,0);
INSERT INTO seqtext VALUES(141,29,1,'Je pousse ce sentiment de ct, ralisant que je divague. Je n''ai ni tourn une page du livre, ni fait quoi que ce soit pour Rin.',1,0,0);
INSERT INTO seqtext VALUES(141,30,1,'Elle essaye de verser de la peinture d''un grand pot en utilisant uniquement son pied, n''ayant pas pris la peine de me demander. Ou peut-tre qu''elle l''a fait, et je ne l''ai pas entendue.',1,0,0);
INSERT INTO seqtext VALUES(141,31,1,'De toute faon, a a l''air instable.',1,0,0);
INSERT INTO seqtext VALUES(141,32,2,'',242,0,0);
INSERT INTO seqtext VALUES(141,33,3,'rin',226,560,10);
INSERT INTO seqtext VALUES(141,33,1,'Je me lve rapidement pour l''aider, on dirait qu''elle est sur le point de dverser la totalit du contenu du pot sur le sol.',1,0,0);
INSERT INTO seqtext VALUES(141,34,1,'Je prends le pot de son pied et mets un peu de peinture dans le bol.',1,0,0);
INSERT INTO seqtext VALUES(141,35,3,'rin',347,560,10);
INSERT INTO seqtext VALUES(141,35,1,'Elle ne dit rien, et moi non plus. Je croise  peine son regard, pos silencieusement sur moi depuis le dessous de sa frange hirsute.',1,0,0);
INSERT INTO seqtext VALUES(141,36,1,'C''est une expression illisible, un visage impassible parfait, mlang  un soupon de quelque chose comme une lgre surprise.',1,0,0);
INSERT INTO seqtext VALUES(141,37,1,'Je me demande ce qu''elle pense. Peut-tre qu''elle se demande ce que je pense. Peut-tre rien.',1,0,0);
INSERT INTO seqtext VALUES(141,38,1,'Je paierais cher pour savoir ce que tu penses.',12,0,0);
INSERT INTO seqtext VALUES(141,39,3,'rin',104,560,10);
INSERT INTO seqtext VALUES(141,39,1,'Tu as de l''argent sur toi ?',15,0,0);
INSERT INTO seqtext VALUES(141,40,1,'Je ne crois pas.',12,0,0);
INSERT INTO seqtext VALUES(141,41,3,'rin',223,560,10);
INSERT INTO seqtext VALUES(141,41,1,'Alors je ne crois pas que je te le dirai. Je ne pense rien non plus, donc tu as de la chance. Sauf que maintenant je viens de le faire.',15,0,0);
INSERT INTO seqtext VALUES(141,42,1,'Elle fronce les sourcils, ayant l''air trs contrarie.',1,0,0);
INSERT INTO seqtext VALUES(141,43,1,'C''est dur de ne penser  rien. Mais c''est important.',15,0,0);
INSERT INTO seqtext VALUES(141,44,1,'Je suis sur le point de demander en quoi c''est important, quand un vieil homme marche vers nous, ayant l''air d''avoir affaire avec Rin.',1,0,0);
INSERT INTO seqtext VALUES(141,45,2,'',454,0,0);
INSERT INTO seqtext VALUES(141,46,3,'nomiya',490,400,10);
INSERT INTO seqtext VALUES(141,46,1,'Bonjour. a avance ?',39,0,0);
INSERT INTO seqtext VALUES(141,47,3,'nomiya',490,240,10);
INSERT INTO seqtext VALUES(141,47,3,'bg',454,400,10);
INSERT INTO seqtext VALUES(141,47,3,'rin',518,560,10);
INSERT INTO seqtext VALUES(141,47,1,'Je peux le faire.',15,0,0);
INSERT INTO seqtext VALUES(141,48,1,'Rin ne s''arrte pas de regarder le mur et rpond tellement naturellement que je ne peux que penser qu''ils se connaissent tous les deux.',1,0,0);
INSERT INTO seqtext VALUES(141,49,1,'Je n''avais jamais vu cet homme auparavant, donc je me demande naturellement qui il est. Peut-tre un professeur ?',1,0,0);
INSERT INTO seqtext VALUES(141,50,1,'Ses cheveux se sont estomps dans un gris tellement argent, qu''ils semblent colors, j''espre que ce n''est pas le cas.',1,0,0);
INSERT INTO seqtext VALUES(141,51,1,'Des petites lunettes rondes sur le bout du nez, mais il semble qu''il soit constamment en train de regarder par dessus, plutt qu'' travers elles.',1,0,0);
INSERT INTO seqtext VALUES(141,52,1,'Il regarde la peinture murale attentivement par-dessus les dites lunettes.',1,0,0);
INSERT INTO seqtext VALUES(141,53,3,'nomiya',587,240,10);
INSERT INTO seqtext VALUES(141,53,1,'Bien, bien.',39,0,0);
INSERT INTO seqtext VALUES(141,54,1,'Quelle composition audacieuse tu as ici !',39,0,0);
INSERT INTO seqtext VALUES(141,55,1,'Il bouge pour inspecter le mur de plus prs, se parlant  lui-mme d''une faon qui rend vident le fait qu''il veuille qu''on l''entende aussi.',1,0,0);
INSERT INTO seqtext VALUES(141,56,3,'nomiya',429,240,10);
INSERT INTO seqtext VALUES(141,56,1,'Trs bien, vraiment trs bien...',39,0,0);
INSERT INTO seqtext VALUES(141,57,1,'Je ne sais pas vraiment quoi faire, mais Rin ne semble pas s''en proccuper. Elle est en train de regarder autour d''elle les bols de diffrents tons disperss un peu partout.',1,0,0);
INSERT INTO seqtext VALUES(141,58,3,'rin',8,560,10);
INSERT INTO seqtext VALUES(141,58,1,'Hisao.',15,0,0);
INSERT INTO seqtext VALUES(141,59,1,'Hmm ?',12,0,0);
INSERT INTO seqtext VALUES(141,60,3,'rin',184,560,10);
INSERT INTO seqtext VALUES(141,60,1,'Un peu plus de a.',15,0,0);
INSERT INTO seqtext VALUES(141,61,1,'Donne-moi une seconde.',12,0,0);
INSERT INTO seqtext VALUES(141,62,1,'Je verse un mlange 50-50 de deux peintures dans le bol pour crer plus du mme ton rose ple que Rin utilisait pour remplir la figure du visage humain.',1,0,0);
INSERT INTO seqtext VALUES(141,63,1,'Rin me regarde faire, ce qui me rend quelque peu nerveux. Son visage est tellement inanim que j''ai l''impression qu''elle est juste en train d''attendre que je fasse quelque chose de travers.',1,0,0);
INSERT INTO seqtext VALUES(141,64,1,'L''homme se tourne vers moi aussi, ayant l''air surpris comme s''il venait juste de remarquer ma prsence.',1,0,0);
INSERT INTO seqtext VALUES(141,65,1,'...Peut-tre que c''est le cas.',1,0,0);
INSERT INTO seqtext VALUES(141,66,3,'nomiya',587,240,10);
INSERT INTO seqtext VALUES(141,66,1,'Tiens, bonjour  toi. Qui es-tu ?',39,0,0);
INSERT INTO seqtext VALUES(141,67,1,'Ah, je suis un tudiant transfr dans la classe 3-3. Hisao Nakai. Enchant.',12,0,0);
INSERT INTO seqtext VALUES(141,68,3,'nomiya',490,240,10);
INSERT INTO seqtext VALUES(141,68,1,'La classe de Mutou, hein ? Eh bien, je ne retiendrai pas a contre toi !',39,0,0);
INSERT INTO seqtext VALUES(141,69,3,'nomiya',429,240,10);
INSERT INTO seqtext VALUES(141,69,1,'Il rit trs fort. Odieusement fort. Quelques petits oiseaux s''envolent depuis un arbre voisin.',1,0,0);
INSERT INTO seqtext VALUES(141,70,3,'nomiya',587,240,10);
INSERT INTO seqtext VALUES(141,70,1,'Je suis Shinichi Nomiya, le professeur d''art.',39,0,0);
INSERT INTO seqtext VALUES(141,71,1,'Donc c''est le professeur d''art. Rtrospectivement, j''aurais d m''en douter. Il en a l''air, dans la mesure des premires impressions.',1,0,0);
INSERT INTO seqtext VALUES(141,72,3,'nomiya',490,240,10);
INSERT INTO seqtext VALUES(141,72,1,'Comment en es-tu venu  assister ma protge ?',23,0,0);
INSERT INTO seqtext VALUES(143,1,1,'Je crois que je suis un peu intress par le club d''art.',12,0,0);
INSERT INTO seqtext VALUES(143,2,1,'Je le laisse chapper, en partie par inadvertance.',1,0,0);
INSERT INTO seqtext VALUES(143,3,3,'nomiya',587,240,10);
INSERT INTO seqtext VALUES(143,3,1,'Qu''est-ce que tu veux dire ?',23,0,0);
INSERT INTO seqtext VALUES(143,4,1,'Rien de... spcifique.',12,0,0);
INSERT INTO seqtext VALUES(143,5,1,'Je me demande si je pourrais venir une fois. Mme si c''est juste pour regarder ou quelque chose comme a.',12,0,0);
INSERT INTO seqtext VALUES(143,6,1,'Je rflchissais au fait que je devrais rejoindre un club ou un truc, alors...',12,0,0);
INSERT INTO seqtext VALUES(143,7,1,'Ce n''est d''aucune faon un geste prmdit, mais un vague sens de la dtermination s''est vraiment construit en moi cette semaine.',1,0,0);
INSERT INTO seqtext VALUES(143,8,1,'Je veux faire quelque chose. Je veux faire partie de quelque chose.',1,0,0);
INSERT INTO seqtext VALUES(143,9,1,'a pourrait bien tre le club d''art, malgr mes dfauts.',1,0,0);
INSERT INTO seqtext VALUES(143,10,1,'Le professeur semble content.',1,0,0);
INSERT INTO seqtext VALUES(143,11,3,'nomiya',429,240,10);
