INSERT INTO seqtext VALUES(146,59,1,'En mme temps, la semaine passe me laisse trs fatigu, donc je lis un peu et vais au lit.',1,0,0);
INSERT INTO seqtext VALUES(146,60,2,'',190,0,0);
INSERT INTO seqtext VALUES(147,1,1,'Merde, pourquoi est-ce que tout le monde insiste pour se mler de mes affaires ? J''imagine que l''infirmier est autorise, voir oblige mais...',1,0,0);
