INSERT INTO seqtext VALUES(148,1,2,'',451,0,0);
INSERT INTO seqtext VALUES(148,2,3,'hanako',438,650,10);
INSERT INTO seqtext VALUES(148,2,1,'Mais pourquoi est-ce que Lilly la laisserait  ses dpens ? a semble tre plutt trange, voyant la raction de Hanako.',1,0,0);
INSERT INTO seqtext VALUES(148,3,1,'',1,0,0);
INSERT INTO seqtext VALUES(148,4,1,'...Ah, c''est vrai. Je crois que Lilly a mentionn quelque chose sur le fait d''aller en ville aujourd''hui avant que Rin ne nous rentre dedans.',1,0,0);
INSERT INTO seqtext VALUES(148,5,1,'L''ide de la promenade me fait regarder  l''extrieur.',1,0,0);
INSERT INTO seqtext VALUES(148,6,2,'',95,0,0);
INSERT INTO seqtext VALUES(148,7,1,'Le soleil brillant et les personnes se baladant et profitant de l''aprs-midi me donnent envie de sortir de l''cole, ou au moins de faire quelque chose d''autre que d''tre assis ici.',1,0,0);
INSERT INTO seqtext VALUES(148,8,1,'M''adonnant  un de mes pires vices, la procrastination, je dcide qu''il est mieux d''tudier l''histoire un dimanche. Ou un lundi. Ou n''importe quel jour autre que celui-l.',1,0,0);
INSERT INTO seqtext VALUES(148,9,1,'Je grogne pendant que je me lve de mon sige, dbattant brivement avec moi-mme sur le fait de sortir avec Kenji ou pas.',1,0,0);
INSERT INTO seqtext VALUES(148,10,1,'Il ne m''a pas vraiment l''air d''tre celui qui apprcie le beau temps dehors avec les autres, vraiment. Je pense que je le rattraperai plus tard.',1,0,0);
INSERT INTO seqtext VALUES(148,11,2,'',451,0,0);
INSERT INTO seqtext VALUES(148,12,1,'Changeant de plan, je pense brivement  aller parler avec Hanako, mais le temps que je regarde vers son sige, il est vide. Elle a d partir pour la bibliothque.',1,0,0);
INSERT INTO seqtext VALUES(150,1,1,'Suivre Hanako  la bibliothque semble un peu intrusif. Il y avait une raison pour qu''elle parte de la classe, aprs tout.',1,0,0);
INSERT INTO seqtext VALUES(150,2,1,'Et outre a, je veux rattraper Lilly. Au moins, je voudrais la remercier de veiller sur moi malgr ses autres devoirs en plus.',1,0,0);
INSERT INTO seqtext VALUES(150,3,1,'Je crois que je vais aller faire un tour en ville. Avec de la chance, je pourrai trouver Lilly. De l''exercice me fera du bien aussi.',1,0,0);
INSERT INTO seqtext VALUES(150,4,2,'',186,0,0);
INSERT INTO seqtext VALUES(150,5,1,'Marchant dans la cour de l''cole vers le portail, je salue de la tte un couple d''tudiants qui passent, geste qu''ils me renvoient par politesse.',1,0,0);
INSERT INTO seqtext VALUES(150,6,1,'Mme d''ici, les cris des membres des clubs de sport peuvent tre entendus. Vu le vacarme, la piste doit tre remplie en ce moment.',1,0,0);
INSERT INTO seqtext VALUES(150,7,1,'Je me rappelle ce qu''a dit Lilly hier sur le fait d''tre parachut directement au milieu de la priode de prparation du festival.',1,0,0);
INSERT INTO seqtext VALUES(150,8,1,'Pendant que j''essaye de prendre mes marques et de rattraper mon retard sur les tudes, tout le monde fait des activits scolaires normales. Le sentiment d''tre un tranger ne s''est toujours pas dissip. Du moins, pas encore.',1,0,0);
INSERT INTO seqtext VALUES(150,9,1,'Bah, je pense que tout n''est pas si mal.',1,0,0);
INSERT INTO seqtext VALUES(150,10,1,'C''est une cole prive, c''est encore plus facilement remarquable quand on marche  l''extrieur. Pas seulement pour l''norme terrain que prend l''cole, mais les btiments eux-mmes sont immaculs et situs loin des coles publiques.',1,0,0);
INSERT INTO seqtext VALUES(150,11,1,'Il y a aussi le fait qu''il y a un fort sentiment de communaut ici, ou du moins une atmosphre amicale. Dans mon ancienne cole, il tait gnralement convenu que les gens restent avec leur petit groupe et fassent avec.',1,0,0);
INSERT INTO seqtext VALUES(150,12,2,'',295,0,0);
INSERT INTO seqtext VALUES(150,13,1,'Je franchis finalement le portail, et commence  marcher vers la ville.',1,0,0);
INSERT INTO seqtext VALUES(150,14,2,'',397,0,0);
INSERT INTO seqtext VALUES(150,15,1,'Eh bien, c''tait assez productif.',1,0,0);
INSERT INTO seqtext VALUES(150,16,1,'Ayant vu une bonne portion de la ville, mme les maisons perches sur les montagnes  la priphrie, je dcide d''aller me balader au parc avant de rentrer.',1,0,0);
INSERT INTO seqtext VALUES(150,17,1,'Ayant vcu toute ma vie dans une ville, l''absence totale d''hommes d''affaires et de filles habilles  la mode me semble vraiment trange.',1,0,0);
INSERT INTO seqtext VALUES(150,18,1,'Tout ce qui peut tre vu sont des tranges vieilles personnes marchant sur les trottoirs et des groupes de femmes d''ge moyen occupes  bavarder devant des petits magasins.',1,0,0);
INSERT INTO seqtext VALUES(150,19,1,'Marcher le long du chemin du parc me distrait rapidement cependant, me faisant raliser que j''ai peut-tre pouss plus loin que je ne l''aurais d juste pour en voir autant que je pouvais.',1,0,0);
INSERT INTO seqtext VALUES(150,20,1,'Vu que ma respiration commence  se faire courte et que ma poitrine se resserre de plus en plus, je renonce  la perspective d''une vie de soldat.',1,0,0);
INSERT INTO seqtext VALUES(150,21,2,'',183,0,0);
INSERT INTO seqtext VALUES(150,22,1,'Aprs un coup d''il rapide dans le parc en entrant, je m''assieds sur un vieux banc bancal d''o je remarque deux distributeurs.',1,0,0);
INSERT INTO seqtext VALUES(150,23,1,'Pendant quelques minutes je reste assis la tte penche en avant, me forant  prendre de grandes respirations. J''ai davantage l''impression d''tre un vieillard qu''un adolescent dans la fleur de l''ge.',1,0,0);
INSERT INTO seqtext VALUES(150,24,1,'Le sjour  l''hpital, la chirurgie et les mdicaments doivent faire effet sur moi. Merde.',1,0,0);
INSERT INTO seqtext VALUES(150,25,1,'Finalement, ma respiration revient  la normale et les muscles dans ma poitrine se desserrent, non sans m''apporter un soulagement. Je suppose que a signifie la fin de ma petite pause ici, en tout cas.',1,0,0);
INSERT INTO seqtext VALUES(150,26,2,'',551,0,0);
INSERT INTO seqtext VALUES(150,27,1,'Il y a un caf au coin, donc je m''arrte pour tancher ma soif avant de rentrer.',1,0,0);
INSERT INTO seqtext VALUES(150,28,2,'',520,0,0);
INSERT INTO seqtext VALUES(150,29,1,'Une clochette au-dessus de la porte signale mon arrive au comptoir vide.',1,0,0);
INSERT INTO seqtext VALUES(150,30,1,'Pendant quelques instants, je suis l  attendre, mon regard se promne d''un bout du comptoir  l''autre  la recherche de la cloche de service.',1,0,0);
INSERT INTO seqtext VALUES(150,31,3,'yuuko',552,400,10);
INSERT INTO seqtext VALUES(150,31,1,'Finalement, une porte plus loin derrire le comptoir s''ouvre, et la personne qui apparat me prend compltement par surprise.',1,0,0);
INSERT INTO seqtext VALUES(150,32,1,'Yu... Yuuko ?',12,0,0);
INSERT INTO seqtext VALUES(150,33,1,'Salut, je ne savais pas que vous travailliez ici.',12,0,0);
INSERT INTO seqtext VALUES(150,34,1,'Je n''ai vraiment aucune ide de comment m''adresser  elle, tant donn qu''elle est techniquement du personnel de l''cole ainsi que, apparemment, une serveuse.',1,0,0);
INSERT INTO seqtext VALUES(150,35,3,'yuuko',320,400,10);
INSERT INTO seqtext VALUES(150,35,1,'Ah, oui, hum...',24,0,0);
INSERT INTO seqtext VALUES(150,36,1,'Elle avance rapidement vers le comptoir, avant de jeter la moiti suprieure de son corps en avant dans un arc trop accentu.',1,0,0);
