INSERT INTO seqtext VALUES(150,43,4,'yuuko',0,0,0);
INSERT INTO seqtext VALUES(150,44,1,'Obissant  ses instructions, je me retourne et jette un rapide coup d''il pour trouver une table libre. Considrant que le caf semble tre vide, c''est plutt facile  faire.',1,0,0);
INSERT INTO seqtext VALUES(150,45,3,'bg',520,320,10);
