INSERT INTO seqtext VALUES(150,46,3,'lilly',163,240,10);
INSERT INTO seqtext VALUES(150,46,3,'akira',135,560,10);
INSERT INTO seqtext VALUES(150,46,1,'Effectivement, rien de plus qu''un coup d''il me suffit pour tre sr que c''est bien Satou  la table. Cela dit, je ne reconnais pas la personne en face d''elle.',1,0,0);
INSERT INTO seqtext VALUES(150,47,1,'Les cheveux blonds, la peau claire et de taille juste un peu plus petite, il... elle ? Je crois. Elle doit tre de sa famille.',1,0,0);
INSERT INTO seqtext VALUES(150,48,1,'Vu qu''elles sont toutes deux plutt silencieuses et que la jeune personne prend une gorge de caf, je dcide de saluer Lilly. Une de mes raisons pour venir ici tait d''aller  sa rencontre, aprs tout.',1,0,0);
