INSERT INTO seqtext VALUES(150,62,3,'akira',435,650,10);
INSERT INTO seqtext VALUES(150,62,3,'yuuko',552,400,10);
INSERT INTO seqtext VALUES(150,62,1,'Pendant qu''elle fait a, Yuuko arrive et pose avec prcaution le caf sur la table avant de s''incliner et de prendre cong.',1,0,0);
